import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';


class Index extends Component {

    componentDidMount(){
        $(document).ready(function() {
            // Transition effect for navbar 
            $(window).scroll(function() {
              // checks if window is scrolled more than 500px, adds/removes solid class
              if($(this).scrollTop() > 300) { 
                  $('.navbar').addClass('SolidNav');
              }
              else {
                  $('.navbar').removeClass('SolidNav');
              }
            });
    });
    }
    render() { 
        
          return ( 
            <Layout>
            <div className="mainnav" >
            <nav className="navbar fixed-top navbar-expand-lg">
            <a className="navbar-brand" href="#">Navbar</a>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                <div className="navbar-nav">
                <Link href="/"><a className="nav-link active font1" >Home</a></Link>
                <Link href="/menu2"><a className=" nav-link active font1" >menu2</a></Link>
                <Link href="/menu1"><a className=" nav-link active font1" >menu1</a></Link>
                </div>
            </div>
            </nav>
              
            </div>
            
            <style jsx>
                {`
                .mainnav {
                    z-index : 100;
                  
                }
                .navbar-nav a {
                    color : white;
                    
                }
                .navbar-nav a:hover {
                    color : gray;
                    
                }
               
                .navbar {
                    z-index : 100;
                    height: 80px;
                    background-color: transparent;
                    border: none;
                    color: white;
                    transition: background-color 2s ease 0s;
               }
              
                .navbar.SolidNav {
                    background-color: red;
                    transition: background-color 2s ease 0s;
                    box-shadow: 0 0 4px grey;
                    color: white;
               }
           
                `}
            </style>
                  </Layout>
           );
      }
    
}

export default Index;
