import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Menu2 from './../components/menu2';
import SubNavBar from './../layouts/SubNavbar';
import Slide from  './../components/Slide1';
import Footer from './../components/Footer';
import $ from 'jquery';
class Index extends Component {

    componentDidMount(){
        $(document).ready(function() {
           
          
            function checkWidth() {
                var windowwidth = $(window).width();
                var windowheight = $(window).height();
                    //if the window is greater than 440px wide then turn on jScrollPane..
                  //  $('.div1').addClass('desktop')
                  $('.silde2image').css({'height':windowwidth/3,'width':'100%'});
                 
               
            }
            // Execute on load
            checkWidth();
            // Bind event listener
            $(window).resize(checkWidth);
    });
    }
    
    render() { 
        
    
          return ( 
            <Layout>
                <div className="container">
                <div className="row">
                <div className="col-lg-6 col-sm-12">
                <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
            <ol className="carousel-indicators">
                <li data-target="#carouselExampleIndicators" data-slide-to="0" className="active"></li>
                <li data-target="#carouselExampleIndicators" data-slide-to="1"></li>
                <li data-target="#carouselExampleIndicators" data-slide-to="2"></li>
            </ol>
            <div className="carousel-inner">
                <div className="carousel-item active">
                <img className="d-block w-100 silde2image" src="./item1.jpg" alt="First slide" width="100%"/>
                </div>
                <div className="carousel-item ">
                <img className="d-block w-100 silde2image" src="./item2.jpg" alt="Second slide" width="100%" />
                </div>
                <div className="carousel-item ">
                <img className="d-block w-100 silde2image" src="./item3.jpg" alt="Third slide" width="100%" />
                </div>
            </div>
            <a className="carousel-control-prev" href="#carouselExampleIndicators" role="button" data-slide="prev">
                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                <span className="sr-only">Previous</span>
            </a>
            <a className="carousel-control-next" href="#carouselExampleIndicators" role="button" data-slide="next">
                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                <span className="sr-only">Next</span>
            </a>
            </div>
                </div>

                <div className="col-lg-6 col-sm-12">
 <div className="col-sm-6 col-md-4 col-lg-3 mt-4">
                <div className="card">
                    <div className="card-block">
                    
                        <h4 className="card-title font2 topicColor">topic in sri lanaka</h4>
                        <div className="meta font6 subtopicColor">
                            <a>head phone</a>
                            <div className=" float-right"> 
                            <a className="Icutprise font6"> <strike>Rs.7500.00</strike>  </a> 
                            <a className="Idiscount font6"> 33% </a>
                            <a className="Iprise font6">&nbsp;Rs.451.25</a>
                            </div>
                        </div>
                        <div className="card-text font6">
                        The . slide class adds a CSS transition and animation effect, which makes the items slide when showing a new item. Omit this class if you do not want this effect. The data-ride="carousel" attribute tells Bootstrap to begin animating the carousel immediately when the page loads.
                        The . slide class adds a CSS transition and animation effect, which makes the items slide when showing a new item. Omit this class if you do not want this effect. The data-ride="carousel" attribute tells Bootstrap to begin animating the carousel immediately when the page loads.
                        The . slide class adds a CSS transition and animation effect, which makes the items slide when showing a new item. Omit this class if you do not want this effect. The data-ride="carousel" attribute tells Bootstrap to begin animating the carousel immediately when the page loads.
                        </div>
                    </div>
                    <div className="card-footer">
                        <small className="font3">2020/7/05</small>
                         <a href="#" className="btn btn-danger float-right btn-sm"><img src="https://img.icons8.com/ios/25/ffffff/favorite-cart.png"/></a>
                         <a href="#" className="btn btn-primary float-right btn-sm"><img src="https://img.icons8.com/ios/25/ffffff/in-transit.png"/></a>
                       
                    </div>
                </div>
        
                   
                    </div>
                </div>

                </div>
                </div>

                <style jsx>
                {`
                h5 {
                    font-size: 1.28571429em;
                    font-weight: 700;
                    line-height: 1.2857em;
                    margin: 0;
                }
                .menu2{
                    margin-bottom : 20px;
                }
                .menu2-topic{
                    padding : 20px 10px 10px 10px;
                }
                .card {
                    font-size: 1em;
                    overflow: hidden;
                    padding: 0;
                    border: none;
                    border-radius: .28571429rem;
                    box-shadow: 0 1px 3px 0 #d4d4d5, 0 0 0 1px #d4d4d5;
                    transition: 0.5s;
                }
                .card:hover{
                    box-shadow: 0 5px 5px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
                    transform: scale(1.02);
                    transition: 0.5s;
                }
                .card-block {
                    font-size: 1em;
                    position: relative;
                    margin: 0;
                    padding: 0.5em;
                    border: none;
                    border-top: 1px solid rgba(34, 36, 38, .1);
                    box-shadow: none;
                }
                
                .card-img-top {
                    display: block;
                    width: 100%;
                    height: 200;
                }
                
                .card-title {
                    font-size: 1.28571429em;
                    font-weight: 700;
                    line-height: 1.2857em;
                    margin-bottom: 0px;
                    
                }
                
                .card-text {
                    clear: both;
                    margin-top: .5em;
                    color: rgba(0, 0, 0, .68);
                    font-size : 12px;
                }
                
                .card-footer {
                    font-size: 1em;
                    position: static;
                    top: 0;
                    left: 0;
                    max-width: 100%;
                    padding: .5em 0.75em;
                    border-top: 1px solid rgba(0, 0, 0, .05) !important;
                    background: #fff;
                }
                
                .card-inverse .btn {
                    border: 1px solid rgba(0, 0, 0, .05);
                }
                
                .profile {
                    position: absolute;
                    top: -12px;
                    display: inline-block;
                    overflow: hidden;
                    box-sizing: border-box;
                    width: 25px;
                    height: 25px;
                    margin: 0;
                    border: 1px solid #fff;
                    border-radius: 50%;
                }
                
                .profile-avatar {
                    display: block;
                    width: 100%;
                    height: 100%;
                    border-radius: 50%;
                }
                
                .profile-inline {
                    position: relative;
                    top: 0;
                    display: inline-block;
                }
                
                .profile-inline ~ .card-title {
                    display: inline-block;
                    margin-left: 4px;
                    vertical-align: top;
                }
                
                .text-bold {
                    font-weight: 700;
                }
                
                .meta {
                    font-size: 1em;
                    
                }
                
                .meta a {
                    text-decoration: none;
                    
                }
                .Icutprise{
                    text-decoration: line-through;
                    color: rgba(0, 0, 0, .8);
                    font-size: 0.75em;
                }
                .Idiscount{
                    color: darkorange;
                    font-size: 0.8em;
                }
                .Iprise{
                    color: red;
                    font-size: 1em;
                }
                `}
                </style>
                   </Layout>
           );
      }
    
}

export default Index;
