import fetch from 'isomorphic-unfetch'

const Show = props => (
  <Layout>
<h1> {props.name}</h1>

  </Layout>
)

Show.getInitialProps = async function(context) {
  const { id } = context.query
  var result=null;
  const res = await fetch(`http://localhost:8000/myshop/idea-mart`);
  console.log(res)
  fetch('/api/myshop/idea-mart',{
    method: 'POST',
    headers: {
    },
    body:''

    }
)
.then(response => { return response.json(); } )
.then(data => { if(data!=undefined){console(data);}})
.catch(error => console.log(error))
    //  const data = await res.json()

  console.log(`Fetched show: `)

  return {name:'bdb'}
}

