import React, { Component } from 'react';
import Link from 'next/link';
import Layout from './../layouts/MainLayout';
import $ from 'jquery';


class Index extends Component {

  componentDidMount= ()=> {

    $(document).ready(function() {
      $('.open-side-navbar').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable side-nav-visible');
      });

      $('.js-menu-close').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable').removeClass(' side-nav-visible');
      })

    });

  }

  showSidebar=()=>{
    $(document).ready(function() {
        $('.js-side-nav-container').addClass('side-nav-animatable side-nav-visible');
    })
  }
  
  
render(){
  return(
<Layout>
<button onClick={this.showSidebar}>gggg</button>

<div className="js-side-nav-container side-nav-container">
<div className="js-side-nav side-nav">
  <a href="#0" className="js-menu-close menu-close">
  <img src="https://img.icons8.com/windows/32/ffffff/delete-slide.png"/>
  </a>
  <div className="sidenavbar-link">
                <Link href="/"><a className="nav-link font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>Home</a></Link>
                <Link href="/menu2"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu2</a></Link>
                <Link href="/menu1"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu1</a></Link>
                <Link href="/menu1"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu1</a></Link>
                <Link href="/"><a className="nav-link font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>Home</a></Link>
                <Link href="/menu2"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu2</a></Link>
                <Link href="/menu1"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu1</a></Link>
                <Link href="/menu1"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu1</a></Link>
                <Link href="/"><a className="nav-link font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>Home</a></Link>
                <Link href="/menu2"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu2</a></Link>
                <Link href="/menu1"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu1</a></Link>
                <Link href="/menu1"><a className=" nav-link  font1" ><img src="https://img.icons8.com/small/30/ffffff/arrow.png"/>menu1</a></Link>
                
                </div>
</div>
</div>


  <style jsx>
  {`



.menu-close{
color: black;
font-size: 36px;
padding: 10px;
background: rgba(7, 36, 159 );
}
.side-nav {
position: relative;
transition: transform 0.35s ease-out;
background: rgba(7, 36, 159 ,0.9);
height: 100%;
width: 90%;
max-width: 300px;
box-shadow: 0 5px 6px 4px rgba(0, 0, 0, .2);
transform: translateX(-102%);
will-change: transform;
display: flex;
flex-direction: column;
}
.side-nav-container {
position: fixed;
left: 0;
top: 0;
width: 100%;
height: 100%;
overflow: hidden;
pointer-events: none;
}
.side-nav-container:before {
transition: opacity 0.3s cubic-bezier(0, 0, 0.3, 1);
content: '';
display: block;
position: absolute;
top: 0;
right: 0;
bottom: 0;
left: 0;
background: rgba(0, 0, 0, .4);
will-change: opacity;
opacity: 0;
pointer-events: none;
}
.side-nav-animatable .side-nav {
transition: transform 0.33s cubic-bexier(0, 0, 0.3, 0.1);
}
.side-nav-animatable.side-nav-visible .side-nav {
transition: transform 0.33s cubic-bezier(0, 0, 0.3, 1);
}
.side-nav-visible {
pointer-events: auto;
}
.side-nav-visible .side-nav {
transform: none;
}
.side-nav-visible:before {
opacity: 1;
}
.sidenavbar-link{
    padding:10%;
    overflow: auto;
}
.sidenavbar-link a{
    color:white;
    border-bottom: 1px solid rgba(0, 0, 0, 0.25) ;
}
  `}
  </style>
</Layout>
  )
}
 
}



export default Index;
