import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import $ from 'jquery';
import SideNav from './../layouts/SideNav';
import GoogleSign from './../components/GoogleSign';
import ReactCrop from 'react-image-crop';
import Router from 'next/router';

class Index extends Component {

  constructor() {
    super();
    this.state = {
      crop: {
        unit: 'px', // default, can be 'px' or '%'
        x: 130,
        y: 50,
        width: 200,
        height: 200
      }
    }

  }

  componentDidMount= ()=> {

  }
  showsidebar(){
    this.refs.child.showSidebar();
  }

  onClickhh=()=>{
alert('ghh');

  $('button').attr("disabled", true);


   //Router.push('/hello-nextjs')


  }

  
  
render(){

  const sidenavlink = [
    {id:1,link:'/',linkname:'home'},
    {id:1,link:'/menu',linkname:'menu'},
    {id:1,link:'/',linkname:'menu2'},
    {id:1,link:'/',linkname:'menu3'},
    {id:1,link:'/',linkname:'menu4'},
    {id:1,link:'/',linkname:'menu5'},
  ];
  const topic = 'Categeries';
  return(
<Layout>


<h1 className="font1">Catageries</h1>
<h1 className="font2">Catageries</h1>
<h1 className="font3">Catageries</h1>
<h1 className="font4">Catageries</h1>
<h1 className="font5">Catageries</h1>
<h1 className="font6">Catageries</h1>
<h1 className="font1 topicColor">Catageries</h1>
<h1 className="font1 subtopicColor">Catageries</h1>


<GoogleSign></GoogleSign>

<div className="col-3">
<button onClick={this.onClickhh} >click</button>
</div>


<div className="col-sm-6 col-md-4 col-lg-3 mt-4">
<div className="row">

<div className="col-4 m-image">
<img className="card-img-top" src={'./form2.jpg'} />
</div>

<div className="col-8 m-content">
<h4 className="card-title font2 topicColor">Idea mart</h4>
<div className="meta font6 subtopicColor">
                            <a>Topic in item</a>
                            <div className=" float-right"> 
                            <a className="Catagory-style font6">&nbsp;Eloctronic Devices</a>
                            </div>
                        </div>
                        
</div>

</div>

</div>



        
                  
                    <div className="col-sm-6 col-md-4 col-lg-3 mt-4">
                <div className="card">
               
                    <div className="card-img-top-div">
                    <img className="card-img-top" src={'./form2.jpg'} height="150px" />
                    </div>

                    <div className="card-block">
                        <figure className="profile">
                            <img src={'./form2.jpg'} className="profile-avatar" alt=""/>
                        </figure>
                        <h4 className="card-title font2 topicColor">Idea mart</h4>
                        <div className="meta font6 subtopicColor">
                            <a>Topic in item</a>
                            <div className=" float-right"> 
                            <a className="Catagory-style font6">&nbsp;Eloctronic Devices</a>
                            </div>
                        </div>
                        <div className="card-text font6 ">
                        Consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore etae magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                        </div>
                    </div>
                    
                    <div className="card-footer">
                        <a className="font3 float-left">2017/7/12</a>
                        <a className="Location-style font6"> {'Colombo'}->{'Negegoda'} </a>
                    </div>
                </div>
        </div>




<br/><br/>
        <style jsx>{
                    `
          
                    .card {
                        font-size: 1em;
                        overflow: hidden;
                        padding: 0;
                        border: none;
                        border-radius: .28571429rem;
                        box-shadow: 0 1px 3px 0 #d4d4d5, 0 0 0 1px #d4d4d5;
                        transition: 0.5s;
                        font-size: 0.9em;
                    }
                    .card:hover{
                        box-shadow: 0 5px 5px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
                        transform: scale(1.02);
                        transition: 0.5s;
                    }
                    .card-block {
                        font-size: 1em;
                        position: relative;
                        margin: 0;
                        padding: 0.5em;
                        border: none;
                        border-top: 1px solid rgba(34, 36, 38, .1);
                        box-shadow: none;
                    }
                    .card-img-top-div{
                        height: 100;
                        overflow : hidden;
                    }
                    
                    .card-img-top {
                        display: block;
                        width: 100%;
                        height: 200;
                    }
                    
                    .card-title {
                        font-size: 1.28571429em;
                        font-weight: 700;
                        line-height: 1.2857em;
                        margin-bottom: 0px;
                        
                    }
                    
                    .card-text {
                        clear: both;
                        margin-top: .5em;
                        color: rgba(0, 0, 0, .68);
                        font-size : 0.9em;
                    }
                    
                    .card-footer {
                        font-size: 1em;
                        position: static;
                        top: 0;
                        left: 0;
                        max-width: 100%;
                        padding: .5em 0.75em;
                        border-top: 1px solid rgba(0, 0, 0, .05) !important;
                        background: #fff;
                    }
                    
                    .card-inverse .btn {
                        border: 1px solid rgba(0, 0, 0, .05);
                    }
                    
                    .profile {
                        position: absolute;
                        top: -12px;
                        display: inline-block;
                        overflow: hidden;
                        box-sizing: border-box;
                        width: 25px;
                        height: 25px;
                        margin: 0;
                        border: 1px solid #fff;
                        border-radius: 50%;
                    }
                    
                    .profile-avatar {
                        display: block;
                        width: 100%;
                        height: 100%;
                        border-radius: 50%;
                    }
                    
                    .profile-inline {
                        position: relative;
                        top: 0;
                        display: inline-block;
                    }
                    
                    .profile-inline ~ .card-title {
                        display: inline-block;
                        margin-left: 4px;
                        vertical-align: top;
                    }
                    
                    .text-bold {
                        font-weight: 700;
                    }
                    
                    .meta {
                        font-size: 1em;
                        
                    }
                    
                    .meta a {
                        text-decoration: none;
                        
                    }
                    .Icutprise{
                        text-decoration: line-through;
                        color: rgba(0, 0, 0, .8);
                        font-size: 0.75em;
                    }
                    .Location-style{
                        color: darkorange;
                        font-size: 1em;
                    }
                    .Catagory-style{
                        color: red;
                        font-size: 1em;
                    }

                    .m-image{
                      width: 100%;
                      padding: 0.1em;
                    }

                    
                    `
                }</style>
                  
              
             

</Layout>
  )
}
 
}



export default Index;
