import React, { Component } from 'react';
import Layout from './../../layouts/MainLayout';
import Allcatagery from '../../components/Allshops';
import SubNavBar from './../../layouts/SubNavbar';
import Slide from  './../../components/Slide1';
import Footer from './../../components/Footer';
import {Url} from './../../constant/main';

class Index extends Component {

    constructor() {
        super();
        this.state = {
            catageries : [],
            allshops : [],
        }

    }
    componentDidMount(){
        this.setState({
            catageries:this.props.allcatagery,
            allshops: this.props.allshops
        })
    }

    showsidebar(){
        this.refs.child.showSidebar();
      }
    
    filterarray(type){
        var array = this.props.allshops;
       var x=  array.filter(x=>
            x.categery == type
            )
        return x;
    }

    render() { 
        
        //side navbar link
        const sidenavlink = [
            {id:1,link:'/',linkname:'home'},
            {id:1,link:'/menu',linkname:'menu'},
            {id:1,link:'/',linkname:'menu2'},
            {id:1,link:'/',linkname:'menu3'},
            {id:1,link:'/',linkname:'menu4'},
            {id:1,link:'/',linkname:'menu5'},
          ];
          
            const sidenavconst = {topic : 'Categeries',topiclink:'all categories',sidenavlink:this.props.allcatagery,visible:true};
        //////////////
          return ( 
            <Layout>
                <SubNavBar sidenavconst={sidenavconst}/>
                
                {this.props.allcatagery.map((c,i)=>
                 <Allcatagery key={i}  allshops={this.filterarray(c.type)} topic={c.name}></Allcatagery>   
                    )}
                


            <Footer/>
                   </Layout>
           );
      }
    
}

Index.getInitialProps = async function(context) {
    const { id } = context.query;
    const resallshop = await fetch(`${Url}allshop/${id}`);
    const resallcatageries = await fetch(`${Url}types`);

    var allshops = await resallshop.json();
    var allcatagery = await resallcatageries.json();

    var error = false;
    if(resallshop.status!=200){
        error = true ;
   }
   console.log(resallcatageries)
    return {itemname:id,allcatagery,allshops,error}


  }

export default Index;
