Index.getInitialProps = async function(context) {
    const { id,shopname } = context.query;
    
    const res = await fetch(`${Url}itembyid/${id}`);
    var  item = await res.json();
    var error = false;
    if(res.status!=200){
        error = true ;
   }

    return {itemid:id,item:item[0],error,shopname:shopname}


  }


