import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';

import Cookie from "js-cookie";

class Index extends Component {

    constructor() {
		super();
		this.state = {
       

  }
	}
        //show signup
        showsignup(){
            this.refs.signup.showsignup();
          }

    componentDidMount(){
 
        $(document).ready(function() {
            
            function checkMobile() {
                var windowwidth = $(window).width();

                if(windowwidth<990){
                 
                    $(window).scroll(function() {
                     
                        
                      });
                }
                  
               
            }
            checkMobile();
            $(window).resize(checkMobile);
    });
    }
    render() { 
        
          return ( 
            <Layout>
                <div  className="col-lg-11 col-sm-12 mx-auto">
            <div className="row filter-section" >
                <div className="field-wrap  col-lg-6 col-md-3 col-sm-6">
                <label  className="font2 labelf1">Search<span className="req">*</span></label>
                <input  className={'font6 inputf1 '} type="text" required  name="itemname"  />
                </div>
                <div className="field-wrap  col-lg-2 col-md-3 col-sm-6">
                <label  className="font2 labelf1">category/sub category</label>
                <input  className={'font6 inputf1 '} type="text" required  name="itemname"  />
                <input  className={'font6 inputf1 '} type="text" required  name="itemname"  />
                </div>
                <div className="field-wrap  col-lg-2 col-md-3 col-sm-6">
                <label  className="font2 labelf1">district/town</label>
                <input  className={'font6 inputf1 '} type="text" required  name="itemname"  />
                <input  className={'font6 inputf1 '} type="text" required  name="itemname"  />
                </div>
                <div className="field-wrap  col-lg-2 col-md-3 col-sm-6">
                <label  className="font2 labelf1">price<span className="req">*</span></label>
                <input  className={'font6 inputf1 '} type="text" required  name="itemname"  />
                <input  className={'font6 inputf1 '} type="text" required  name="itemname"  />
                </div>
                </div>
              
            </div>
            <style jsx>
            {`
            .filter-section{
                background:white;
            }
            .labelf1 {
                position: relative;
                transform: translateY(10px);
                left: 13px;
                color: #01567e;
                transition: all 0.25s ease;
                -webkit-backface-visibility: hidden;
                pointer-events: none;
                font-size: 18px;
            }
            .inputf1 {
                font-size: 18px;
                display: block;
                width: 100%;
                padding: 5px 10px;
                background: #c2d1e17d;
                background-image: none;
                border: none;
                border: 1px solid #01567e;
                color: darkblue;
                border-radius: 0;
                transition: border-color 0.5s ease;
            }
            .inputf1:focus, textarea:focus {
                outline: 0;
                border-color: #023957;
            }
            `}
            </style>
                 </Layout>
           );
      }
    
}

export default Index;
