import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';
import {Url} from './../constant/main';

import Cookie from "js-cookie";

class Index extends Component {

    constructor() {
		super();
		this.state = {
            types:[],
            categery:'',
            subcategery:'',
            district:'',
            town:'',
            upperPrice:'',
            lowerPrice:''

  }
	}
        //show signup
    handleChange = evt => {
            
        this.setState({
            [evt.target.name]: evt.target.value,
        });
        
    };

    gettows=(district)=>{

        var id = this.props.location.findIndex(e=>e.district==district);
        var index = id>0?id:0;

       return district.length>2?this.props.location[index].town:[];
    }
    getsubcategory=(categery)=>{

        var id = this.state.types.findIndex(e=>e.type==categery);
        var index = id>0?id:0;

       return categery.length>1?this.state.types[index]:[];
    }

    componentDidMount(){

        fetch(`${Url}types`)
        .then(res=>{return res.json()})
        .then(data=>{this.setState({types:data});})
       
        $(document).ready(function() {
            
            function checkMobile() {
                var windowwidth = $(window).width();

                if(windowwidth<990){
                 
                    $(window).scroll(function() {
                     
                        
                      });
                }
                  
               
            }
            checkMobile();
            $(window).resize(checkMobile);
    });
    }
    render() { 
        
          return ( 
            <Layout>
                <div  className=" filter-section">
            <div className="row col-lg-11 col-sm-12 mx-auto" >

                <div className="field-wrap  col-lg-6 col-md-3 col-sm-6">
                <label  className="font1 labelf1">Search</label>
                <input  className={'font1 inputf1 '} type="text" required  name="itemname"  />
                <p>what are your choise?</p>
                </div>

                <div className="field-wrap  col-lg-2 col-md-3 col-sm-6">
                <label  className="font1 labelf1">Category</label>
                <select className={'font1 inputf1'} type="text" required  name="categery" value={this.state.categery} onChange={this.handleChange}>
                <option key='1' value="d">Default select</option>
                {this.state.types.map((x,i)=><option key={i} value={x.type}>{x.name}</option>)}                      
                </select>
                <select className={'font1 inputf1'} type="text" required  name="subcategery" value={this.state.subcategery} onChange={this.handleChange}>
                <option key='1' value="d">Default select</option>
                {this.getsubcategory(this.state.categery).map((x,i)=><option key={i} value={x.type}>{x.name}</option>)}                      
                </select>
               
                </div>

                <div className="field-wrap  col-lg-2 col-md-3 col-sm-6">
                <label  className="font1 labelf1">Location</label>
                <input  className={'font1 inputf1 '} type="text" required  name="itemname"  />
                <input  className={'font1 inputf1 '} type="text" required  name="itemname"  />
                </div>

                <div className="field-wrap  col-lg-2 col-md-3 col-sm-6">
                <label  className="font1 labelf1">Price</label>
                <input  className={'font1 inputf1 '} type="text" required  name="itemname"  />
                <input  className={'font1 inputf1 '} type="text" required  name="itemname"  />
                </div>

                </div>
                <br></br>
            </div>
           
            <style jsx>
            {`
            .filter-section{
                background:#c2d1e17d;
            }
            .labelf1 {
                position: relative;
                transform: translateY(10px);
                left: 5px;
                color: #01567e;
                transition: all 0.25s ease;
                -webkit-backface-visibility: hidden;
                pointer-events: none;
                font-size: 15px;
            }
            .inputf1 {
                font-size: 15px;
                display: block;
                width: 100%;
                padding: 5px 5px;
                background: white;
                background-image: none;
                border: none;
                border: 1px solid #01567e;
                color: darkblue;
                border-radius: 0;
                transition: border-color 0.5s ease;
            }
            .inputf1:focus, textarea:focus {
                outline: 0;
                border-color: #023957;
            }
            `}
            </style>
                 </Layout>
           );
      }
    
}



export default Index;
