class Index extends Component {


    state = {
        name : 'chanaka',
        items : [],
        totalprice : 0,

    }


    updateOrder(id,qty){
        var tempitem = this.state.items;
        var itemsIndex = tempitem.findIndex(function(c) { 
            return c.id == id; 
        });

        tempitem[itemsIndex].qty = tempitem[itemsIndex].qty+qty;
        if(tempitem[itemsIndex].qty>=0){
            this.setState({
                items : [...tempitem],
    
            })
        }
     
    }

    componentDidMount(){
        $(document).ready(function() {
           
          
            function checkWidth() {
                var windowwidth = $('.silde2image').width();
                var windowheight = $(window).height();
                    //if the window is greater than 440px wide then turn on jScrollPane..
                  //  $('.div1').addClass('desktop')
                  $('.silde2image').css({'max-height':windowwidth/3*2,'width':'100%'});
                  $('.silde2image').parent().css({'height':windowwidth/3*2,'width':'100%','background': '#ffffff'});
               
            }
            // Execute on load
            checkWidth();
            // Bind event listener
            $(window).resize(checkWidth);
    });
    this.setState({
        items : this.props.items
    })
    }

    
    render() { 

          return ( 
            <Layout>
             
                <div className="cart-menu col-lg-11 col-sm-12 mx-auto">
                <div>
                    <h2 className="font1 topicColor profile-css-topic">{this.props.topic}</h2>
                    
                </div>
                <div className="row col-12">
                <Imageside shop={this.props.shop?this.props.shop:null} ></Imageside>
                <Contentside shop={this.props.shop?this.props.shop:null} ></Contentside>
                </div>
                <br/>  
                <CartList items={this.state.items} catagerytype="Phones" updateOrder={(id,qty)=>this.updateOrder(id,qty)}/>
                </div>  
                  </Layout>
           );
      }
    
}

  
  export default Index;
