import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';


class Index extends Component {

  componentDidMount= ()=> {

    $(document).ready(function() {
      $('.js-menu-open').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable side-nav-visible')
      });

      $('.js-menu-close').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable').removeClass(' side-nav-visible');
      })

    });
    this.sideNavEl = document.querySelector('.js-side-nav');
    this.sideNavContainerEl = document.querySelector('.js-side-nav-container');
    this.showButtonEl = document.querySelector('.js-menu-open');
    this.closeButtonEl = document.querySelector('.js-menu-close');
    
  }
  
  addEventListeners=()=> {
    this.showButtonEl.addEventListener('click', this.openSideNav);
    this.closeButtonEl.addEventListener('click', this.closeSideNav);
    this.sideNavEl.addEventListener('click', this.blockClicks);
    this.sideNavContainerEl.addEventListener('click', this.closeSideNav);
  }
 
  blockClicks=(evt)=> {
    evt.stopPropagation();
  }
  
  onTransitionEnd=(evt)=> {
    this.sideNavContainerEl.classList.remove('side-nav-animatable');
    this.sideNavContainerEl.removeEventListener('transitionend', this.onTransitionEnd);
  }
  
  openSideNav=()=> {
    this.sideNavContainerEl.classList.add('side-nav-animatable');
    this.sideNavContainerEl.classList.add('side-nav-visible');
    this.sideNavContainerEl.addEventListener('transitionend', this.onTransitionEnd);
  }
  
  closeSideNav=()=> {
    this.sideNavContainerEl.classList.add('side-nav-animatable');
    this.sideNavContainerEl.classList.remove('side-nav-visible');
    this.sideNavContainerEl.addEventListener('transitionend', this.onTransitionEnd);
  }
  
render(){
  return(
<Layout>


<h1 className="font1">Catageries</h1>
<h1 className="font2">Catageries</h1>
<h1 className="font3">Catageries</h1>
<h1 className="font4">Catageries</h1>
<h1 className="font5">Catageries</h1>
<h1 className="font6">Catageries</h1>
<h1 className="font1 topicColor">Catageries</h1>
<h1 className="font1 subtopicColor">Catageries</h1>

<div className="container">
<nav>
<a href="#0" className="js-menu-open menu-open">
  <i className="material-icons">menu</i>
</a>
</nav>
<div className="js-side-nav-container side-nav-container">
<div className="js-side-nav side-nav">
  <a href="#0" className="js-menu-close menu-close">
    <i className="material-icons">close</i>
  </a>
</div>
</div>
</div>


  <style jsx>
  {`
html {
margin: 0;
padding: 0;
box-sizing: border-box;
}
nav {
height: 60px;
width: 100%;
background: #282828;
display: flex;
flex: row;
align-items: center;
}
.menu-open i {
color: white;
font-size: 36px;
padding-left: 10px;
}
.menu-close i {
color: black;
font-size: 36px;
padding: 10px;
}
.side-nav {
position: relative;
transition: transform 0.35s ease-out;
background: #f0f0f0;
height: 100%;
width: 90%;
max-width: 400px;
box-shadow: 0 5px 6px 4px rgba(0, 0, 0, .2);
transform: translateX(-102%);
will-change: transform;
display: flex;
flex-direction: column;
}
.side-nav-container {
position: fixed;
left: 0;
top: 0;
width: 100%;
height: 100%;
overflow: hidden;
pointer-events: none;
}
.side-nav-container:before {
transition: opacity 0.3s cubic-bezier(0, 0, 0.3, 1);
content: '';
display: block;
position: absolute;
top: 0;
right: 0;
bottom: 0;
left: 0;
background: rgba(0, 0, 0, .4);
will-change: opacity;
opacity: 0;
pointer-events: none;
}
.side-nav-animatable .side-nav {
transition: transform 0.33s cubic-bexier(0, 0, 0.3, 0.1);
}
.side-nav-animatable.side-nav-visible .side-nav {
transition: transform 0.33s cubic-bezier(0, 0, 0.3, 1);
}
.side-nav-visible {
pointer-events: auto;
}
.side-nav-visible .side-nav {
transform: none;
}
.side-nav-visible:before {
opacity: 1;
}


  `}
  </style>
</Layout>
  )
}
 
}



export default Index;
