import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import SubNavBar from './../layouts/SubNavbar';
import Footer from './../components/Footer';
import fetch from 'isomorphic-unfetch';
import $ from 'jquery';
class Index extends Component {

    constructor() {
        super();
        this.state = {
            shopName: 'jb mh',
            categery :'',
            town:'',
            address:'',
            contact1:'',
            contact2:'',
            content1:'',
            content2:'',
            newshopdetail:'',
            shopDetail:[{name:'cash on delivery',value:''},{name:'double',value:'yes'}],
            selectedFile : null
        };
    }
    componentDidMount(){
        $(document).ready(function() {
            $('.form').find('.f1').on('keyup blur focus', function (e) {
  
                var $this = $(this),
                    label = $this.prev('label');
              
                    if (e.type === 'keyup') {
                          if ($this.val() === '') {
                        label.removeClass('active highlight');
                      } else {
                        label.addClass('active highlight');
                      }
                  } else if (e.type === 'blur') {
                      if( $this.val() === '' ) {
                          label.removeClass('active highlight'); 
                          } else {
                          label.removeClass('highlight');   
                          }   
                  } else if (e.type === 'focus') {
                    
                    if( $this.val() === '' ) {
                          label.removeClass('highlight'); 
                          } 
                    else if( $this.val() !== '' ) {
                          label.addClass('highlight');
                          }
                  }
              
              });
              
              function loadingform1(){
                $('.form f1').each(
                    function(){
                        var val = $(this).val().trim();
                        if (val != ''){
                            $(this).prev('label').addClass('active highlight');
                        }
                    });
              }
         
            loadingform1();
              
        })
    }

    handleChange = evt => {
    // This triggers everytime the input is changed
        this.setState({
            [evt.target.name]: evt.target.value,
        });
    };
 
    handleChangedetails= evt =>{
        var shopd = this.state.shopDetail;
        shopd.find(function(e){
             if(e.name == evt.target.name){
                 e.value = evt.target.value;
             }
        });

        this.setState({
            shopDetail : shopd
        })
    }
    addnewShopDetails = evt =>{
        var shopd = this.state.shopDetail;
        shopd.push({name:this.state.newshopdetail,value:''});
        this.setState({
            shopDetail : shopd,
            newshopdetail : ''
        })
        alert(' added new details!');
        this.componentDidMount();

        console.log(this.state.shopDetail)
    }
    deleteDetals = value =>{

        if(confirm('is it sure remove '+value+'?')){

        
       var shopd = this.state.shopDetail;
       shopd.splice(shopd.findIndex(function(e){
        return e.name == value
     
        }),1);

       this.setState({
        shopDetail : shopd,
    })
    this.componentDidMount();
    }

    }
    handleSubmit = evt => {
        evt.preventDefault();
        fetch('/api/all',{
            method: 'POST',
            headers: {
            },
            body:'sanjea'
        
            }
        )
        .then((response) => {
            return response.json();
        })
        .then((data) => {
            console.log(data);
        });

    };

    onChangeHandler=event=>{


        console.log(event.target.files[0])
        this.setState({
          selectedFile: event.target.files[0],

        })
        
    
    }

    
      

    showsidebar(){
        this.refs.child.showSidebar();
      }
    
    render() { 
        
        const items = [
            {id:1 , topic: 'topic1', subtopic:'subtopic1',imageUrl:'http://loremflickr.com/320/150?random=1'   ,discount:5,date:'2020/4/5',price:150,topicImageUrl:'https://img.icons8.com/ios/50/000000/contract-job.png',content:'In publishing, art, and communication, content is the information and experiences that are directed toward an end-user or audience. Content is "something that is to be expressed through some medium, as speech, writing or any of various arts',qty:0,seller:'idea mart'},
