import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import { GoogleLogin } from 'react-google-login';
import SubNavBar from './../layouts/SubNavbar';
class Index extends Component {

  componentDidMount= ()=> {

  }
  showsidebar(){
    this.refs.child.showSidebar();
  }

  responseGoogle = (response) => {
    console.log(response);
  }
  
render(){
      //side navbar link
      const sidenavlink = [
        {id:1,link:'/',linkname:'home'},
        {id:1,link:'/menu',linkname:'menu'},

      ];
      
        const sidenavconst = {topic : 'Categeries',topiclink:'All ',sidenavlink:sidenavlink};
  return(
<Layout>
<SubNavBar sidenavconst={sidenavconst}/>
<div id="background-signin">
            <GoogleLogin
             clientId="511880674901-gfn6v2n1ej65rrlnnv29odgbjkpkhpcj.apps.googleusercontent.com"
             buttonText="Sign In"
            onSuccess={this.responseGoogle}
            onFailure={this.responseGoogle}
            cookiePolicy={'single_host_origin'}
            cssClassnclassName="btn btn-outline-primary"
            isSignedIn={false}
            />


    <div id="logreg-forms" className="col-lg-4 col-md-8 col-sm-12">
        
            <h1 className="h3 mb-3 font-weight-normal" > Sign in</h1>
            <div className="social-login">
                <button className="btn facebook-btn social-btn" type="button"><span><i className="fab fa-facebook-f"></i> Sign in with Facebook</span> </button>
                <button className="btn google-btn social-btn" type="button"><span><i className="fab fa-google-plus-g"></i> Sign in with Google+</span> </button>
            </div>
            <h1 className="h3 mb-3 font-weight-normal" > Sign Up</h1>
            <form className="form-signin">
            <div class="control-group">
                            <label class="control-label" for=
                            "inputCompanyName">Company Name</label>

                            <div class="controls">
                                <input id="inputCompanyName" placeholder=
                                "E.g. Some Software Pvt. Ltd." type="text"/>
                            </div>
                        </div>
            <div className="input-group">
              <input type="email" id="inputEmail" className="form-control" placeholder="Email address" required="" autofocus=""/>
            </div>

            <div className="input-group">
              <input type="password" id="inputPassword" className="form-control" placeholder="Password" required=""/>
            </div>

            <div className="input-group">
              <button className="btn btn-md btn-rounded btn-block form-control submit" type="submit"><i className="fas fa-sign-in-alt"></i> Sign in</button>
            </div>


            <hr/>
            <button className="btn btn-primary btn-block" type="button" id="btn-signup"><i className="fas fa-user-plus"></i> Sign up New Account</button>
            </form>


    </div>
</div>
<style jsx>
{`
#background-signin{
    width: 100%;
    height: auto;
    background: -webkit-linear-gradient(0deg, gary 0%, white 100%);
    font-family: "Robato",sans-serif;
    font-size: 17px;
  }
  
  #logreg-forms{
   
      margin:10vh auto;
      background-color:#ffffff4d;
      box-shadow: 0 7px 7px rgba(0, 0, 0, 0.12), 0 12px 40px rgba(0,0,0,0.24);
    transition: all 0.3s cubic-bezier(.25,.8,.25,1);
  }
  
  #logreg-forms form {
      width: 100%;

      padding: 15px;
      margin: auto;
  }
  #logreg-forms .form-control {
      position: relative;
      box-sizing: border-box;
      height: auto;
      padding: 10px;
      font-size: 16px;
  }
  #logreg-forms .form-control:focus { z-index: 2; }
  #logreg-forms .form-signin input[type="email"] {
      margin-bottom: -1px;
      border-bottom-right-radius: 0;
      border-bottom-left-radius: 0;
  }
  #logreg-forms .form-signin input[type="password"] {
      border-top-left-radius: 0;
      border-top-right-radius: 0;
  }
  
  #logreg-forms .social-login{

      margin:0 auto;
      margin-bottom: 14px;
  }
  #logreg-forms .social-btn{
      font-weight: 100;
      color:white;
      font-size: 0.9rem;
  }
  
  #logreg-forms a{
      display: block;
      padding-top:10px;
      color:#fff;
  }
  
  #logreg-form .lines{
      width:200px;
      border:1px solid red;
  }
  
  
  #logreg-forms button[type="submit"]{ margin-top:10px; }
  
  #logreg-forms .facebook-btn{  background-color:#3C589C; }
  
  #logreg-forms .google-btn{ background-color: #DF4B3B; }
  
  #logreg-forms .form-reset, #logreg-forms .form-signup{ display: none; }
  
  #logreg-forms .form-signup .social-btn{ width:210px; }
  
  #logreg-forms .form-signup input { margin-bottom: 2px;}
  
  .form-signup .social-login{
      margin: 0 auto;
  }
  
  .submit{
    background: -webkit-linear-gradient(0deg,  #2dfbff 0%, #3c96ff 100%);
    border-radius: 25px;
    color: #fff;
  }
  
  /* Mobile */
  
  @media screen and (max-width:500px){
      #logreg-forms{
          width:300px;
      }
  
      #logreg-forms  .social-login{
          width:200px;
          margin:0 auto;
          margin-bottom: 10px;
      }
      #logreg-forms  .social-btn{
          font-size: 1.3rem;
          font-weight: 100;
          color:white;
          height: 56px;
  
      }
      #logreg-forms .social-btn:nth-child(1){
          margin-bottom: 5px;
      }
      #logreg-forms .social-btn span{
          display: none;
      }
      #logreg-forms  .facebook-btn:after{
          content:'Facebook';
      }
  
      #logreg-forms  .google-btn:after{
          content:'Google+';
      }
  
  }
`}
</style>

</Layout>
  )
}
 
}



export default Index;
