import React, { Component } from 'react';
import Container from '@material-ui/core/Container';
import MaterialTable from 'material-table';


class AllVehicles extends Component {
  state = { 
    columns: [
          
      {title: 'Active', render: rowdata => this.Check(rowdata.isValid) , cellStyle: {
        backgroundColor: 'lightgray',
        color: 'red'
      }, },

      { title: '', field: 'imageUrl', render: rowData => <img src={NodeConstant.ImageUrl+rowData.imageUrl} style={{width: 150, borderRadius: '5%'}}/> },
      { title: 'Date', field: 'createDate',  render : rowData => (new Date(rowData.createDate).getMonth()+1)+' / '+new Date(rowData.createDate).getDate()+' in '+new Date(rowData.createDate).getFullYear() },
      { title: 'Vehicle No', field: 'VehicleNo' },
      { title: 'Vehicle barnd', field: 'vehicleName' },
      { title: 'Vehicle Type', field: 'vehicleType' },
      { title: 'others', render: rowdata => 'rate'+rowdata.priceRate+'\n seat:'+rowdata.seat  },
      
      { title: 'Owner Name', field: 'ownerName' },
      { title: 'Owner Email', field: 'ownerEmail' },
      { title: 'Owner Contact',  render: rowdata => 'Tp1:'+rowdata.ownerContact1+'\n Tp2:'+rowdata.ownerContact2 },

      { title: 'Driver ', render: rowdata => rowdata.driverName+'\n (Tp):'+rowdata.contactOfdriver  },

    ],
   }
   componentDidMount(){

   }

   changeState=(event,rowdata,state)=>{
    let id =rowdata._id;

    const response = Api.put('updatevehicle/'+id,{isValid:state})
    .then(res =>{
      this.componentDidMount()
    })
    .catch(error=>{
     
    })
   }

   Check=(condition)=>{

    if(condition == false || condition == 'false'){
      return <img src="https://img.icons8.com/color/48/000000/undelete.png"/>
    }
    else{
      return <img src="https://img.icons8.com/color/50/000000/double-tick.png"/>
    }
  }

  render() { 
     return (
      <Container component="main" maxWidth="lg"  >
      <MaterialTable
        title="All vehicle Owners"
        columns={this.state.columns}
        data={this.state.users}
      
        options={{
          exportButton: true,
          search: true,
          sorting: true,
          headerStyle: {
            backgroundColor: '#01579b',
            color: '#FFF'
          },
          
        }}
       
        actions={[
          {
            icon: 'save',
            tooltip: 'Save Taxi',
            onClick: (event, rowData) => {
              this.changeState(event,rowData,true)
            },
           
          },

          {
            icon: 'info',
            tooltip: 'Edit Taxi',
            onClick: (event, rowData) =>  window.location.pathname='/updatetaxi/'+rowData._id,
           
          },

          {
            icon: 'delete',
            tooltip: 'remove Taxis',
            onClick: (event, rowData) => 
              this.changeState(event,rowData,false)
            
          }
        ]}
      />
      </Container>
    );
  }
}
 
export default AllVehicles;
