import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';


class Index extends Component {

    componentDidMount(){
        $(document).ready(function() {

            $('.togglemenu').click(function(){
                    $('div.navbar-collapse').toggleClass('show');
                    console.log('bfffffffff')
            })
          
            // Transition effect for navbar 
            $(window).scroll(function() {
              // checks if window is scrolled more than 500px, adds/removes solid class
              if($(this).scrollTop() > $(window).height()/2) { 
                  $('.navbar').addClass('SolidNav');
              }
              else {
                  $('.navbar').removeClass('SolidNav');
              }
            });
            
            function checkMobile() {
                var windowwidth = $(window).width();

                if(windowwidth<990){
                    $('.nav-link').css({'background-color':'black','opacity':'0.5','padding-left':'20px'});

                    $(window).scroll(function() {
                        if($(this).scrollTop() > $(window).height()/2) { 
                            $('.nav-link').css({'background-color':'darkblue','opacity':'0.5','padding-left':'20px'});
                        }else{
                            $('.nav-link').css({'background-color':'black','opacity':'0.5','padding-left':'20px'});
                        }
                        
                      });
                }
                  
               
            }
            checkMobile();
            $(window).resize(checkMobile);
    });
    }
    render() { 
        
          return ( 
            <Layout>
            <div className="mainnav" >
            <nav className="navbar fixed-top navbar-expand-lg">
            <a className="navbar-brand font1" href="#">MYBIZ.COM</a>
            <button className="togglemenu"> menu</button>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                <div className="navbar-nav">
                <Link href="/"><a className="nav-link active font1" >Home</a></Link>
                <Link href="/menu2"><a className=" nav-link active font1" >menu2</a></Link>
                <Link href="/menu1"><a className=" nav-link active font1" >menu1</a></Link>
                
                <Link href="/menu3"><a className=" nav-link active font1" >menu3</a></Link>
                <Link href="/menu1"><a className=" nav-link active font1" >menu1</a></Link>
                </div>
            </div>
       
            
            </nav>

            
              
            </div>
            
            <style jsx>
                {`
                .mainnav {
                    z-index : 100;
                  
                }
                .navbar-nav{
                    padding-left : 100px;
                }
              
                .navbar-nav a {
                    color : white;
                    
                }
                .navbar-nav a:hover {
                    color : gray;
                    
                }
               
                .navbar {
                    z-index : 100;
                    height: 80px;
                    background-color: transparent;
                    border: none;
                    color: white;
                    transition: background-color 2s ease 0s;
               }
              
                .navbar.SolidNav {
                    background-color: darkblue;
                    transition: background-color 2s ease 0s;
                    box-shadow: 0 0 4px grey;
                    color: white;
               }
           
               .navbar-toggler{
                background-color: gray;
               }
                `}
            </style>
                  </Layout>
           );
      }
    
}

export default Index;
