import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import { GoogleLogin } from 'react-google-login';
import $ from 'jquery';
class Index extends Component {

  componentDidMount= ()=> {

    $(document).ready(function() {

  
        $('.popup-close').click(function(){
          $('#background-signin').css({'display':'none'}).removeClass('visual-signup');
        })

  
      });

  }


  responseGoogle = (response) => {
    console.log(response);
  }

  showsignup=()=>{
    $(document).ready(function() {
        $('#background-signin').css({'display':'flex'}).addClass('visual-signup');
    })
  }
  
render(){

  return(
<Layout>

<div id="background-signin">


    <div id="logreg-forms" className="col-lg-4 col-md-8 col-sm-12">
    <div className="popup-close">x</div>
            <br/>
            <h3 className="h3 mb-3 font6 topicColor" > Sign in</h3>
            <div className="social-login row col-12">
                <button className="btn facebook-btn social-btn col-lg-6 col-sm-12" type="button"><span><i className="fab fa-facebook-f"></i> Sign in with Facebook</span> </button>
                <GoogleLogin
                    clientId="511880674901-gfn6v2n1ej65rrlnnv29odgbjkpkhpcj.apps.googleusercontent.com"
                    buttonText="Sign in with Google+"
                    onSuccess={this.responseGoogle}
                    onFailure={this.responseGoogle}
                    cookiePolicy={'single_host_origin'}
                    className="btn google-btn social-btn col-lg-6 col-sm-12"
                    isSignedIn={false}
                    />
            </div>
            
            <h3 className="h3 mb-3 font6 topicColor" > Sign up</h3>
            <form>
            <div className="form-group">
                <label for="exampleInputAddress" className="font6">address</label>
                <input type="email" className="form-control" id="exampleInputAddress" placeholder="Enter Address"/>
            </div>
            <div className="form-group">
                <label for="exampleInputAddress" className="font6">Contact</label>
                <input type="email" className="form-control" id="exampleInputAddress" placeholder="Enter Contact"/>
            </div>
            <div className="form-check">
                <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                <label className="form-check-label" for="exampleCheck1" className="font6">Sign up as Seller</label>
            </div>
            <small id="emailHelp" className="form-text text-muted">We'll never share your details with anyone else.</small>
            <div className="social-login row col-12">
                <button className="btn facebook-btn social-btn col-lg-6 col-sm-12" type="button"><span><i className="fab fa-facebook-f"></i> Sign Up with Facebook</span> </button>
                <GoogleLogin
                    clientId="511880674901-gfn6v2n1ej65rrlnnv29odgbjkpkhpcj.apps.googleusercontent.com"
                    buttonText="Sign in with Google+"
                    onSuccess={this.responseGoogle}
                    onFailure={this.responseGoogle}
                    cookiePolicy={'single_host_origin'}
                    className="btn google-btn social-btn col-lg-6 col-sm-12"
                    isSignedIn={false}
                    />
            </div>
            </form>
                </div>
</div>
<style jsx>
{`

.visual-signup {
    transition: transform 0.33s cubic-bezier(0, 0, 0.3, 1);
}
.popup-close {
	color: darkred;
	display: flex;
	align-items: center;
	justify-content: center;
	background: #343434;
	cursor: pointer;
	font-size: 1.0rem;
	width: 2.0rem;
	height: 2.0rem;
	top: 1.0rem;
	right: 1.00rem;
	position: absolute;
	border-radius: 100%;
}
#background-signin{
    width: 100%;
    height: auto;
    background: -webkit-linear-gradient(0deg, gray 0%, white 100%);
    font-family: "Robato",sans-serif;
    font-size: 17px;
    display:none;
  }
  
  #logreg-forms{
    box-shadow: 0 7px 7px rgba(0, 0, 0, 0.12), 0 12px 40px rgba(0,0,0,0.24);
      margin:10vh auto;
      background-color: white;
      z-index : 50;
  }
  
  #logreg-forms form {
      width: 100%;
      
      padding: 15px;
      margin: auto;
  }
  #logreg-forms .form-control {
      position: relative;
      box-sizing: border-box;
      height: auto;
      padding: 10px;
      font-size: 16px;
  }
  #logreg-forms .form-control:focus { z-index: 2; }
  #logreg-forms .form-signin input[type="email"] {
      margin-bottom: -1px;
      border-bottom-right-radius: 0;
      border-bottom-left-radius: 0;
  }
  #logreg-forms .form-signin input[type="password"] {
      border-top-left-radius: 0;
      border-top-right-radius: 0;
  }
  
  #logreg-forms .social-login{

      margin:0 auto;
      margin-bottom: 14px;
  }
  #logreg-forms .social-btn{
      font-weight: 100;
      color:white;
      font-size: 0.9rem;
  }
  
  #logreg-forms a{
      display: block;
      padding-top:10px;
      color:#fff;
  }
  
  #logreg-form .lines{
      width:200px;
      border:1px solid red;
  }
  
  
  #logreg-forms button[type="submit"]{ margin-top:10px; }
  
  #logreg-forms .facebook-btn{  background-color:#3C589C; }
  
  #logreg-forms .google-btn{ background-color: #DF4B3B;}
  
  #logreg-forms .form-reset, #logreg-forms .form-signup{ display: none; }
  
  #logreg-forms .form-signup .social-btn{ width:210px; }
  
  #logreg-forms .form-signup input { margin-bottom: 2px;}
  
  .form-signup .social-login{
      margin: 0 auto;
  }
  
  .submit{
    background: -webkit-linear-gradient(0deg,  #2dfbff 0%, #3c96ff 100%);
    border-radius: 25px;
    color: #fff;
  }
  
  /* Mobile */
  
  @media screen and (max-width:500px){
      #logreg-forms{
          width:300px;
      }
  
      #logreg-forms  .social-login{
          width:200px;
          margin:0 auto;
          margin-bottom: 10px;
      }
      #logreg-forms  .social-btn{
          font-size: 1.3rem;
          font-weight: 100;
          color:white;
          height: 56px;
  
      }
      #logreg-forms .social-btn:nth-child(1){
          margin-bottom: 5px;
      }
      #logreg-forms .social-btn span{
          display: none;
      }
      #logreg-forms  .facebook-btn:after{
          content:'Facebook';
      }
  
      #logreg-forms  .google-btn:after{
          content:'Google+';
      }
  
  }
`}
</style>

</Layout>
  )
}
 
}



export default Index;
