import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import $ from 'jquery';
import SubNavBar from './../layouts/SubNavbar';
import {Url,ImageUrl} from './../constant/main'

class Index extends Component {

  constructor() {
    super();
    this.state = {
        items:[]
    }

  }

  componentDidMount= ()=> {

    this.setState({
        items:this.props.items
    })
  }

  
render(){

    const sidenavconst = {topic : 'Categeries',topiclink:'All Categeriess',sidenavlink:[], visible:false };
  return(
<Layout>
<SubNavBar sidenavconst={sidenavconst}/> 
<br/>              

<table class="table table-striped container">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>

{this.state.items.map((x,i)=>
    <tr>
    <th> <img width="50px" src={ImageUrl+x.images[0]} /></th>
    <td> 
        <div> <p>{x.itemname} </p>
            <p>{x.subcategery} </p>
        </div> 
    </td>
    <td>{x.itemPrice}</td>
    <td>chanaka sanjeewA</td>
    </tr>
    )}

  </tbody>
</table>
</Layout>
  )
}
 
}


  Index.getInitialProps = async function(context) {
    const { id } = context.query;
    
    const res = await fetch(`${Url}orderbyshopid/${id}`);
    var  item = await res.json();
    var error = false;
 //   if(res.status!=200){
        error = true ;
  // }

    return {item:item,error}


  }

export default Index;
