Index.getInitialProps = async function(context) {
    const { id } = context.query;
    
    const res = await fetch(`${Url}typebyid/${id}`);
    var  types = await res.json();
    var error = false;
    if(res.status!=200){
        error = true ;
   }

    return {typeid:id,type:types[0],error}


  }

export default Index;
