import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import SubNavBar from './../layouts/SubNavbar';
import Footer from './../components/Footer';
import fetch from 'isomorphic-unfetch';
import $ from 'jquery';
class Index extends Component {

    constructor() {
        super();
        this.state = {
            shopName: '',
            categery :'',
            town:'',
            address:'',
            contact1:'',
            contact2:'',
            content1:'',
            content2:'',
            shopDetail:[{name:'cash on delivery',value:''},{name:'yes',value:''}],
            selectedFile : null
        };
    }
    componentDidMount(){
        $(document).ready(function() {
            $('.form').find('input, textarea').on('keyup blur focus', function (e) {
  
                var $this = $(this),
                    label = $this.prev('label');
              
                    if (e.type === 'keyup') {
                          if ($this.val() === '') {
                        label.removeClass('active highlight');
                      } else {
                        label.addClass('active highlight');
                      }
                  } else if (e.type === 'blur') {
                      if( $this.val() === '' ) {
                          label.removeClass('active highlight'); 
                          } else {
                          label.removeClass('highlight');   
                          }   
                  } else if (e.type === 'focus') {
                    
                    if( $this.val() === '' ) {
                          label.removeClass('highlight'); 
                          } 
                    else if( $this.val() !== '' ) {
                          label.addClass('highlight');
                          }
                  }
              
              });
              
              
        })
    }

    handleChange = evt => {
    // This triggers everytime the input is changed
        this.setState({
            [evt.target.name]: evt.target.value,
        });
    };
 
    handleChangedetails= evt =>{
        var shopd = this.state.shopDetail;
        var x =shopd.push({[evt.target.name]: evt.target.value})
        this.setState({
            shopDetail : x
        })
    }
    handleSubmit = evt => {
        evt.preventDefault();
        fetch('/api/all',{
            method: 'POST',
            headers: {
            },
            body:'sanjea'
        
            }
        )
        .then((response) => {
            return response.json();
        })
        .then((data) => {
            console.log(data);
        });

    };

    onChangeHandler=event=>{


        console.log(event.target.files[0])
        this.setState({
          selectedFile: event.target.files[0],

        })
        
    
    }

    
      

    showsidebar(){
        this.refs.child.showSidebar();
      }
    
    render() { 
        
        const items = [
            {id:1 , topic: 'topic1', subtopic:'subtopic1',imageUrl:'http://loremflickr.com/320/150?random=1'   ,discount:5,date:'2020/4/5',price:150,topicImageUrl:'https://img.icons8.com/ios/50/000000/contract-job.png',content:'In publishing, art, and communication, content is the information and experiences that are directed toward an end-user or audience. Content is "something that is to be expressed through some medium, as speech, writing or any of various arts',qty:0,seller:'idea mart'},
            {id:2 , topic: 'topic2', subtopic:'subtopic1',imageUrl:'http://loremflickr.com/320/150?random=2'   ,discount:5,date:'2020/4/5',price:150,topicImageUrl:'https://img.icons8.com/ios/50/000000/contract-job.png',content:'In publishing, art, and communication, content is the information and experiences that are directed toward an end-user or audience. Content is "something that is to be expressed through some medium, as speech, writing or any of various arts',qty:0,seller:'idea mart'},
            {id:3 , topic: 'topic3', subtopic:'subtopic1',imageUrl:'http://loremflickr.com/320/150?random=3'   ,discount:5,date:'2020/4/5',price:150,topicImageUrl:'https://img.icons8.com/ios/50/000000/contract-job.png',content:'In publishing, art, and communication, content is the information and experiences that are directed toward an end-user or audience. Content is "something that is to be expressed through some medium, as speech, writing or any of various arts',qty:0,seller:'idea mart'},
            {id:4 , topic: 'topic4', subtopic:'subtopic1',imageUrl:'http://loremflickr.com/320/150?random=4'   ,discount:5,date:'2020/4/5',price:150,topicImageUrl:'https://img.icons8.com/ios/50/000000/contract-job.png',content:'In publishing, art, and communication, content is the information and experiences that are directed toward an end-user or audience. Content is "something that is to be expressed through some medium, as speech, writing or any of various arts',qty:0,seller:'idea mart'},
            {id:5 , topic: 'topic5', subtopic:'subtopic1',imageUrl:'http://loremflickr.com/320/150?random=5'   ,discount:5,date:'2020/4/5',price:150,topicImageUrl:'https://img.icons8.com/ios/50/000000/contract-job.png',content:'In publishing, art, and communication, content is the information and experiences that are directed toward an end-user or audience. Content is "something that is to be expressed through some medium, as speech, writing or any of various arts',qty:0,seller:'idea mart'},
         
        ];
        //side navbar link
        const sidenavlink = [
            {id:1,link:'/',linkname:'home'},
            {id:1,link:'/menu',linkname:'menu'},
            {id:1,link:'/',linkname:'menu2'},
            {id:1,link:'/',linkname:'menu3'},
            {id:1,link:'/',linkname:'menu4'},
            {id:1,link:'/',linkname:'menu5'},
          ];
          
            const sidenavconst = {topic : 'Categeries',topiclink:'All Categeriess',sidenavlink:sidenavlink};
        //////////////
          return ( 
            <Layout>
                <SubNavBar sidenavconst={sidenavconst}/>

            <div className="form-create-shop">

                <div className="container" >
                    <h1 className="font4 fontsizeE2-25 topicColor">Create new shop</h1>
                    <form className="form">

                    <div className="content">
                        <h3 className="font4 fontsizeE1-5 fontcolorOrange">Shop Details</h3>
                        <div className="row">
                            <div className="field-wrap  col-lg-6 col-sm-12">
                                <label  className="font2">Shop Name<span className="req">*</span></label>
                                <input className="font6" type="text" required autocomplete="off" name="shopName" value={this.state.shopName} onChange={this.handleChange} />
                            </div>
                            <div className="field-wrap col-lg-6 col-sm-12">
                                <label  className="font2">Categery Name<span className="req">*</span></label>
                                <input className="font6" type="text" required autocomplete="off" name="categery" value={this.state.categery} onChange={this.handleChange} />
                            </div>
                            <div className="field-wrap col-lg-6 col-sm-12">
                                <label  className="font2">Town<span className="req">*</span></label>
                                <input className="font6" type="text" required autocomplete="off" name="town" value={this.state.town} onChange={this.handleChange} />
                            </div>
                            <div className="field-wrap col-lg-6 col-sm-12">
                                <label  className="font2">Address<span className="req">*</span></label>
                                <input className="font6" type="text" required autocomplete="off" name="address" value={this.state.address} onChange={this.handleChange} />
                            </div>
                            <div className="field-wrap col-lg-6 col-sm-12">
                                <label  className="font2">contact 1<span className="req">*</span></label>
                                <input className="font6" type="text" required autocomplete="off" name="contact1" value={this.state.contact1} onChange={this.handleChange} />
                            </div>
                            <div className="field-wrap col-lg-6 col-sm-12">
                                <label  className="font2">contact 2<span className="req">*</span></label>
                                <input className="font6" type="text" required autocomplete="off" name="contact2" value={this.state.contact2} onChange={this.handleChange} />
                            </div>
                            <div className="field-wrap col-lg-12 col-sm-12">
                                <label  className="font2">content 1<span className="req">*</span></label>
                                <textarea className="font6"  rows="3" required autocomplete="off" name="content1" value={this.state.content1} onChange={this.handleChange} />
                            </div>
                            <div className="field-wrap col-lg-12 col-sm-12">
                                <label  className="font2">content 2<span className="req">*</span></label>
                                <textarea className="font6"  rows="3" required autocomplete="off" name="content2" value={this.state.content2} onChange={this.handleChange} />
                            </div>
                           
                        </div>
                        <hr></hr>
                        <div className="content">
                        <h3 className="font4 fontsizeE1-5 fontcolorOrange">Shop Details</h3>
                     
                        <div className="row">
                        {this.state.shopDetail.map(x=>(
                            <div className="field-wrap col-lg-6 col-sm-12">
                            <label  className="font2">{x.name}<span className="req">*</span></label>
                            <input className="font6" type="text" required autocomplete="off" name="shopname" value={x.value} onChange={this.handleChangedetails} />
                        </div>
                        )

                        )}
                        </div>
                        </div>
                    </div>


                    </form>
                </div>

            </div>

<style jsx>
{`

.form-create-shop {
    background-image: url("/form1.jpg");
    background-position: center; /* Center the image */
    background-repeat: no-repeat; /* Do not repeat the image */
}
.container{
    /*background: #dde1ffbf  ;*/
    background-image: url("/form1.jpg");
    background-repeat: no-repeat; /* Do not repeat the image */
    padding : 20px 10px;
}
label {
    position: relative;
    transform: translateY(40px);
    left: 13px;
    color: #016b9d;
    transition: all 0.25s ease;
    -webkit-backface-visibility: hidden;
    pointer-events: none;
    font-size: 18px;
}
label .req {
    margin: 2px;
    color: #016b9d;
}
label.active {
    left: 13px;
    transform: translateY(10px);
    font-size: 15px;
}
label.active .req {
    opacity: 0;
}
