import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';


class Index extends Component {

    componentDidMount(){
        $(document).ready(function() {
            // Transition effect for navbar 
            $(window).scroll(function() {
              // checks if window is scrolled more than 500px, adds/removes solid class
              if($(this).scrollTop() > 300) { 
                  $('.navbar').addClass('solid').fadeIn();
              } else if ($(this).scrollTop()<50){
                $('.navbar').addClass('solid sticky-top').fadeIn();
              }
              else {
                  $('.navbar').removeClass('solid sticky-top fixed-top').fadeOut();
              }
            });
    });
    }
    render() { 
        
          return ( 
            <Layout>
            <div className="mainnav" >
            <nav className="navbar navbar-light">
            <a className="navbar-brand" href="#">Navbar</a>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                <div className="navbar-nav">
                <Link href="/"><a className="nav-link active" >Home</a></Link>
                <Link href="/menu2"><a className=" nav-link active" >menu2</a></Link>
                <Link href="/menu1"><a className=" nav-link active" >menu1</a></Link>
                </div>
            </div>
            </nav>
              
            </div>
            <style jsx>
                {`
                .mainnav {
                   
                   
                    z-index : 100;
                  
                }
                .nav-link {
                    color : white;
                    padding-left : 10%;
                    background-color: gray;
                }
               
                .navbar {
                    height: 80px;
                    background-color: transparent;
                    border: none;
                    color: white;
                    transition: background-color 2s ease 0s;
               }
              
                .navbar.solid {
                    background-color: gray;
                    transition: background-color 2s ease 0s;
                    box-shadow: 0 0 4px grey;
                    color: white;
               }
           
                `}
            </style>
                  </Layout>
           );
      }
    
}

export default Index;
