import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import $ from 'jquery';

const CartList=(props)=>{

   const  unitcount=(id,qty)=>{

        $(document).ready(function() {
            var unitcount = parseInt($('input[type=button]#unitcount-'+id).val())+qty;
            if(unitcount>0)
            {
            $('input[type=button]#unitcount-'+id).val(unitcount);
            }
        });
        props.updateOrder(id,qty)
        
    }

    return(
        <div className="menu2 row projects">
        {props.catageries.map(c=>
        
        <div key={c.id} className="card">
        <div className="row col-12">
        <div className="col-lg-3">
        <img className="f-card-img-top" src={c.imageUrl}/>
        </div>
        <div className="col-lg-9">
        <h4 className="card-title font2 topicColor">{c.topic}</h4>
        <div className="meta font6 subtopicColor">
            <a>{c.topic} - {c.id}</a>
            <div className=" float-right"> 
            <a className="Icutprise font6"> <strike>Rs.{c.price}.00</strike>  </a> 
            <a className="Idiscount font6"> {c.discount}% </a>
            <a className="Iprise font6">&nbsp;Rs.{c.price*(100+c.discount)/100}</a>
            </div>
        </div>
        <div className="card-text font6">
        {c.content.slice(0,150)}...
         </div>
        <div className="card-footer">
        <small className="font3">2020/25/5</small>
        <div class="btn-group float-right" role="group" aria-label="Basic example">
        <input type="button" class="unitcount-text float-right btn btn-danger btn-sm" aria-describedby="emailHelp" placeholder="qty" value='unit'></input>      
        <input type="button" class="unitcount-text float-right btn btn-danger btn-sm" id={'unitcount-'+c.id} aria-describedby="emailHelp" placeholder="qty" value={c.qty}></input>      
        <button type="button" onClick={()=>unitcount(c.id,-1)}  class="btn btn-secondary btn-sm"><img src="https://img.icons8.com/ios/20/ffffff/sort-down.png"/>&nbsp;</button>
        <button type="button" onClick={()=>unitcount(c.id,1)} class="btn btn-secondary btn-sm"><img src="https://img.icons8.com/ios/20/ffffff/sort-up.png"/>&nbsp;</button>
        <button type="button" onClick={()=>unitcount(c.id,10)} class="btn btn-secondary btn-sm">+10</button>
        </div>
        
             </div>
    </div>


</div>    
            </div>
        )}
            <style jsx>{
                    `.unitcount-text{
                        width : 50px;
                    }
                    h5 {
                        font-size: 1.28571429em;
                        font-weight: 700;
                        line-height: 1.2857em;
                        margin: 0;
                    }
                    .menu2{
                        margin-bottom : 20px;
                    }
                    .menu2-topic{
                        padding : 20px 10px 10px 10px;
                    }
                    .card {
                        font-size: 1em;
                        overflow: hidden;
                        padding: 20px;
                        border: none;
                        border-radius: .28571429rem;
                        box-shadow: 0 1px 3px 0 #d4d4d5, 0 0 0 1px #d4d4d5;
                        transition: 0.5s;
                        margin : 0.5em 0 ;
                    }
                    .card:hover{
                        box-shadow: 0 5px 5px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
                        transform: scale(1.001);
                        transition: 0.5s;
                    }
                    .card-block {
                        font-size: 1em;
                        position: relative;
                        margin: 0;
                        padding: 0.5em;
                        border: none;
                        border-top: 1px solid rgba(34, 36, 38, .1);
                        box-shadow: none;
                    }
                    
                    .f-card-img-top {
                        display: block;
                        width: 100%;
                        height: 200;
                     
                    }
                    
                    .card-title {
                        font-size: 1.28571429em;
                        font-weight: 700;
                        line-height: 1.2857em;
                        margin-bottom: 0px;
                        
                    }
                    
                    .card-text {
                        clear: both;
                        margin-top: .5em;
                        color: rgba(0, 0, 0, .68);
                        font-size : 12px;
                    }
                    
                    .card-footer {
                        font-size: 1em;
                        position: static;
                        top: 0;
                        left: 0;
                        max-width: 100%;
                        padding: .5em 0.75em;
                        border-top: 1px solid rgba(0, 0, 0, .05) !important;
                        background: #fff;
                    }
                    
                    .card-inverse .btn {
                        border: 1px solid rgba(0, 0, 0, .05);
                    }
                    
                    .profile {
                        position: absolute;
                        top: -12px;
                        display: inline-block;
                        overflow: hidden;
                        box-sizing: border-box;
                        width: 25px;
                        height: 25px;
                        margin: 0;
                        border: 1px solid #fff;
                        border-radius: 50%;
                    }
                    
                    .profile-avatar {
                        display: block;
                        width: 100%;
                        height: 100%;
                        border-radius: 50%;
                    }
                    
                    .profile-inline {
                        position: relative;
                        top: 0;
                        display: inline-block;
                    }
                    
                    .profile-inline ~ .card-title {
                        display: inline-block;
                        margin-left: 4px;
                        vertical-align: top;
                    }
                    
                    .text-bold {
                        font-weight: 700;
                    }
                    
                    .meta {
                        font-size: 1em;
                        
                    }
                    
                    .meta a {
                        text-decoration: none;
                        
                    }
                    .Icutprise{
                        text-decoration: line-through;
                        color: rgba(0, 0, 0, .8);
                        font-size: 0.75em;
                    }
                    .Idiscount{
                        color: darkorange;
                        font-size: 0.8em;
                    }
                    .Iprise{
                        color: red;
                        font-size: 1em;
                    }

                    .projects div {
                        animation-name: fade-in;
                        animation-fill-mode: both;
                        animation-duration: 1s;
                   }
                    .projects div:nth-of-type(1) {
                        animation-delay: 0.6s;
                   }
                    .projects div:nth-of-type(2) {
                        animation-delay: 0.8s;
                   }
                    .projects div:nth-of-type(3) {
                        animation-delay: 1.0s;
                   }
                    .projects div:nth-of-type(4) {
                        animation-delay: 1.2s;
                   }
                    .projects div:nth-of-type(5) {
                        animation-delay: 1.4s;
                   }
                    .projects div:nth-of-type(6) {
                        animation-delay: 1.6s;
                   }
                    .projects div:nth-of-type(7) {
                        animation-delay: 1.8s;
                   }
                    .projects div:nth-of-type(8) {
                        animation-delay: 2.0s;
                   }
                    .projects div:nth-of-type(9) {
                        animation-delay: 2.2s;
                   }
                    .projects div:nth-of-type(10) {
                        animation-delay: 2.4s;
                   }
                    .projects div:nth-of-type(11) {
                        animation-delay: 2.6s;
                   }
                    .projects div:nth-of-type(12) {
                        animation-delay: 2.8s;
                   }
                    .projects div:nth-of-type(13) {
                        animation-delay: 3.0s;
                   }
                    .projects div:nth-of-type(14) {
                        animation-delay: 3.2s;
                   }
                    .projects div:nth-of-type(15) {
                        animation-delay: 3.4;
                   }
                    @keyframes fade-in {
                        0% {
                            opacity: 0;
                       }
                        100% {
                            opacity: 1;
                       }
                   }

                    `
                }</style>
        </div>
      
    )
}

const OrderSide=(props)=>{
    return(
        <div>
        <h4 className="font4 fontsizeE1-5 fontcolorSkyblue">My Order Bill</h4>

<div class="container">
  <div class="row">
    <div class="col-xs-12">
      <div class="table-responsive">
        <table summary="This table shows how to create responsive tables using Bootstrap's default functionality" class="table table-bordered table-hover">
          <caption class="text-center">An example of a responsive table based on </caption>
          <thead >
            <tr className=" fontcolorOrange">
              <th>Item code</th>
              <th>Name</th>
              <th>Seller</th>
              <th>Discount</th>
              <th>Price</th>
              <th>Qty</th>
              <th className="fontcolorpink">Total</th>
            </tr>
          </thead>
          <tbody >
              {props.cartItems.map(c=>
                  <tr key={c.id}  className="subtopicColor">
                  <td>{c.id}</td>
                  <td>{c.name}</td>
                  <td>{c.seller}</td>
                  <td>{c.discount}</td>
                  <td>{c.price}</td>
                  <td>{c.qty}</td>
                  <td className="fontcolorpink">{c.subtotal}</td>
                </tr>
                )}
          
        </tbody>
          <tfoot>
            <tr>
              <td colspan="6" class="text-center">Your Total price </td>
              <td className="fontcolorred">45454545</td>
            </tr>
          </tfoot>
        </table>
      </div>
    </div>
  </div>
</div>



        </div>
    )
}

class Index extends Component {


    state = {
        name : 'chanaka',
        cartItem : [
            {id:1,qty:0,name:'head set',seller:'idea mart',price:420,discout:10,subtotal:250},
            {id:2,qty:0,name:'head set',seller:'idea ',price:560,discout:11,subtotal:250},
            {id:3,qty:0,name:'head set',seller:' mart',price:420,discout:15,subtotal:250}
        ]
    }

    updateState(name){
        this.setState({
            name :name
        })
    };
    updateOrder(id,qty){
        var temp = this.state.cartItem;
        var tempdataIndex = temp.findIndex(function(c) { 
            return c.id == id; 
        });
        temp[tempdataIndex].qty = temp[tempdataIndex].qty+qty;
        this.setState({
            cartItem : [...temp]
        })
    }
    
    render() { 
        
        
          return ( 
            <Layout>
              
                <div className="cart-menu">
                <div>
                    <h2 className="font1 topicColor menu2-topic">{this.props.topic}</h2>
                    
                </div>
                <div className="row">
                    <div className="col-lg-6 col-sm-12">
                    <CartList catageries={this.props.catageries} updateOrder={(id,qty)=>this.updateOrder(id,qty)}/>
                    </div>
                 
                    <div className="col-lg-6 col-sm-12">
                    <OrderSide name= {this.state.name} cartItems={this.state.cartItem}/>
                    </div>
                </div>
                

                </div>
                <style jsx>
                {`
                .cart-menu{
                    margin : 20px;
                }
                
                `}</style>
                
                  </Layout>
           );
      }
    
}

export default Index;
