import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Menu1 from './../components/menu1';
import NavBar from './../layouts/Navbar';
import $ from 'jquery';


class Index extends Component {

    componentDidMount(){
        $(document).ready(function() {
           
          
            function checkWidth() {
                var windowwidth = $(window).width();
                var windowheight = $(window).height();
                if (windowwidth > 440) {
                    //if the window is greater than 440px wide then turn on jScrollPane..
                  //  $('.div1').addClass('desktop')
                  $('div.div1').css({'background-color':'red','height':windowheight})
                }else{
                    $('div.div1').css({'background-color':'red'})
                }
            }
            // Execute on load
            checkWidth();
            // Bind event listener
            $(window).resize(checkWidth);
    });
    }
    render() { 
        
          return ( 
            <Layout>
         <NavBar></NavBar>
         <div className="div1"  >
         <h1>djvbhj</h1>
        </div> 
            <img src ="./mainimage.jpg" width="100%" />
              <Menu1></Menu1>
     
            <style jsx>
                {`
              div.div1 {
                height: 500px;
                
              }
              div.div1.desktop{
                height: 900px;
              }
            
           
                `}
            </style>
                  </Layout>
           );
      }
    
}

export default Index;
