import React, { Component } from 'react';
import Link from 'next/link';
import Layout from './../layouts/MainLayout';
import $ from 'jquery';


class Index extends Component {

  componentDidMount= ()=> {

    $(document).ready(function() {
      $('.open-side-navbar').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable side-nav-visible');
      });

      $('.js-menu-close').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable').removeClass(' side-nav-visible');
      })

    });

  }

  showSidebar=()=>{
    $(document).ready(function() {
        $('.js-side-nav-container').addClass('side-nav-animatable side-nav-visible');
    })
  }
  
  
render(){
  return(
<Layout>
<button onClick={this.showSidebar}>gggg</button>

<div className="js-side-nav-container side-nav-container">
<div className="js-side-nav side-nav">
  <a href="#0" className="js-menu-close menu-close">
    <i className="material-icons">close</i>
  </a>
  <div className="navbar-nav">
                <Link href="/"><a className="nav-link active font1" >Home</a></Link>
                <Link href="/menu2"><a className=" nav-link active font1" >menu2</a></Link>
                <Link href="/menu1"><a className=" nav-link active font1" >menu1</a></Link>
                
                <Link href="/menu3"><a className=" nav-link active font1" >menu3</a></Link>
                <Link href="/menu1"><a className=" nav-link active font1" >menu1</a></Link>
                
                </div>
</div>
</div>


  <style jsx>
  {`



.menu-close i {
color: black;
font-size: 36px;
padding: 10px;
}
.side-nav {
position: relative;
transition: transform 0.35s ease-out;
background: #f0f0f0;
height: 100%;
width: 90%;
max-width: 400px;
box-shadow: 0 5px 6px 4px rgba(0, 0, 0, .2);
transform: translateX(-102%);
will-change: transform;
display: flex;
flex-direction: column;
}
.side-nav-container {
position: fixed;
left: 0;
top: 0;
width: 100%;
height: 100%;
overflow: hidden;
pointer-events: none;
}
.side-nav-container:before {
transition: opacity 0.3s cubic-bezier(0, 0, 0.3, 1);
content: '';
display: block;
position: absolute;
top: 0;
right: 0;
bottom: 0;
left: 0;
background: rgba(0, 0, 0, .4);
will-change: opacity;
opacity: 0;
pointer-events: none;
}
.side-nav-animatable .side-nav {
transition: transform 0.33s cubic-bexier(0, 0, 0.3, 0.1);
}
.side-nav-animatable.side-nav-visible .side-nav {
transition: transform 0.33s cubic-bezier(0, 0, 0.3, 1);
}
.side-nav-visible {
pointer-events: auto;
}
.side-nav-visible .side-nav {
transform: none;
}
.side-nav-visible:before {
opacity: 1;
}


  `}
  </style>
</Layout>
  )
}
 
}



export default Index;
