import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';
import SideNav from './../layouts/SideNav';
import SignUp from './../components/signup';
import {NavLink,wesitename} from './../constant/main';
import Cookie from "js-cookie";
import Filter from './filterItem'
 

const NavBar=(props)=>{
    return(
        <div>
        <nav className="navbar navbar-expand-md">
       
        { props.sidenavconst.visible?<button onClick={props.showsidebar.bind(this)} type="button" className="btn btn-sm btn-primary font1">{props.sidenavconst.topiclink}</button>:<a className="navbar-brand font7" href="#">{wesitename}a</a>}
  
        <button className="navbar-toggler togglemenuO menuOpen" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
        <img src="https://img.icons8.com/ios/30/ffffff/menu.png"/>
        </button>
        <button className=" navbar-toggler togglemenuC menuClose" type="button">
        <img src="https://img.icons8.com/ios/25/ffffff/delete-sign.png"/>
        </button>
        <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
            <div className="navbar-nav">
            {NavLink.map((x,i)=>
                <Link key={i} href={x.url}><a className="nav-link nav-link-main active font1" >{x.urlname}</a></Link>
                )}
                <Link key='101' href={`/myprofile?id=${props.state.isuserlogin?props.state.user._id:''}`}><a className="nav-link nav-link-main active font1 isuserlogin"  >my-profile</a></Link>
                <a className="nav-link nav-link-main active font1" onClick={props.showsignup.bind(this)} >sign-in</a>
                <a className="nav-link nav-link-main active font1 pointer" onClick={props.showfilter.bind(this)} >filter</a>
                
         </div>
                
          </div>
          {props.state.isuserlogin? <a className="logo-link nav-link nav-link-main active font1" href="#">{props.state.user.givenName} </a>:
        <a className="navbar-brand font7 all-link" href="#">{wesitename}</a>
            }
            {props.state.isuserlogin?<img className="float-left avatar" src={props.state.user.imageUrl} />:null}    
        </nav>
        <style jsx>
                {`
                  .avatar {
                    vertical-align: middle;
                    width: 30px;
                    height: 30px;
                    border-radius: 50%;
                  }
                .subnav {
                    z-index : 100;
                  
                }
                .navbar-nav{
                    padding-left : 100px;
                }
              
                .navbar-nav a {
                    color : white;
                    
                }
                .navbar-nav a:hover {
                    color : gray;
                    
                }
               
                .navbar {
                    z-index : 50;
                    height: 60px;
                    background-color: darkblue;
                    border: none;
                    color: white;
                    transition: background-color 2s ease 0s;
                    background-image: linear-gradient( darkblue,transparent);
               }
              
                .navbar.SolidNav {
                    background-color: darkblue;
                    transition: background-color 2s ease 0s;
                    box-shadow: 0 0 4px grey;
                    color: white;
                    height:50px;
               }
               .togglemenuC{
                display:none
               }
               .sticky {
                position: -webkit-sticky;
                width :100%;
                position: fixed;
                top: 0;
              }
               .all-link{
                   display:none;
               }
               
                `}
            </style>
           
    </div>
    )
}


const MobileNavBar=(props)=>{

    const show=()=>{
        if($('#collapsibleNavbar').is(":visible")){
            $('#collapsibleNavbar').slideUp(500);
        }else{
            $('#collapsibleNavbar').slideDown(500);
        }
    }

    return(
        <div>
            <div className="mobile-nav">
            <nav className="navbar navbar-expand-lg ">
            <span className="navbar-brand">
            {props.state.isuserlogin?<img className="float-left avatar" src={props.state.user.imageUrl} />:null} 
            &nbsp;{ props.sidenavconst.visible?<button onClick={props.showsidebar.bind(this)} type="button" className="btn btn-sm btn-primary font1">{props.sidenavconst.topiclink}</button>:<a className="font7" href="#">{wesitename}</a>}
            &nbsp; <a className=" font1 pointer" onClick={props.showfilter.bind(this)} ><img src="https://img.icons8.com/ios/30/ffffff/filter-edit.png"/></a>
            
            </span>
           
            <button className="navbar-toggler" type="button"  onClick={show} >
                <img src="https://img.icons8.com/ios/30/ffffff/menu.png"/>
            </button>
            <div className="collapse navbar-collapse" id="collapsibleNavbar">
                <ul className="navbar-nav">
                {NavLink.map((x,i)=>
                <Link key={i} href={x.url}><a className="nav-link  active font1" >{x.urlname}</a></Link>
                )}
                <Link key='101' href={`/myprofile?id=${props.state.isuserlogin?props.state.user._id:''}`}><a className="nav-link  active font1 isuserlogin"  >my-profile</a></Link>
                <a className="nav-link  active font1" onClick={props.showsignup.bind(this)} >sign-in</a>
              
                </ul>
            </div>  
            </nav>
            </div>
         <style jsx>
         {`
         .avatar {
            vertical-align: middle;
            width: 30px;
            height: 30px;
            border-radius: 50%;
          }
         .navbar{
            background-color: darkblue;
            background-image: linear-gradient( darkblue,transparent);
         }
         .nav-link{
             color:white;
             padding-left : 10px;
             background-image: linear-gradient( #0b4286,darkblue);
         }

        
         `}
         </style>
        </div>
    )
}

class Index extends Component {
    constructor() {
		super();
		this.state = {
        isuserlogin:false,
        user:{}

  }
	}

    //side nav bar open
    showsidebar(){
        this.refs.sidenav.showSidebar();
      }
    //show signup
      showsignup(){
          $('.filter-item').hide()  
        this.refs.signup.showsignup();
      }

    //showfilter
    showfilter(){
        if($('.filter-item').is(":visible")){
            $('.filter-item').slideUp(1000) 
        }
        else{
          
            $('.filter-item').slideDown(1000)  
        }
    }
    
    componentDidMount(){
        $('.filter-item').hide();
        $('.show-fixed-bar').hide();
        this.setState({
            user : Cookie.getJSON('user'),
            isuserlogin : Cookie.getJSON('user')?true:false
        })

        $(document).ready(function() {

            $('.togglemenuC').click(function(){
                    $('div.navbar-collapse').removeClass('show');
                    $('.menuClose').fadeOut(10);
                    $('.menuOpen').fadeIn(100);
                 
            });
            $('.togglemenuO').click(function(){
                $('.menuOpen').fadeOut(10);
                $('.menuClose').fadeIn(100);
             
            });
          
            // Transition effect for navbar 
            $(window).scroll(function() {
              // checks if window is scrolled more than 500px, adds/removes solid class
              if($(this).scrollTop() >  $(window).width()/6) { 
                  $('.sub1nav').removeClass('sticky-top');
                  $('.show-fixed-bar').slideDown(200);
              }
              else {
                  $('.sub1nav').addClass('sticky-top');
                  $('.show-fixed-bar').slideUp(100);
              }
             
            });
        
    });
    }

    

    render() { 
        
          return ( 
            <Layout>
            {this.props.sidenavconst.visible ?<SideNav ref="sidenav"  sidenavconst={this.props.sidenavconst}/>:null}

            <SignUp ref="signup" showsignup={this.props.showsignup} />
            <div className="subnav sub1nav" >
          {/* <nav className="navbar sticky-top navbar-expand-lg">

            {this.state.isuserlogin?<img className="float-left avatar" src={this.state.user.imageUrl} />:null}
            {this.state.isuserlogin? <a className="logo-link nav-link nav-link-main active font1" href="#">{this.state.user.givenName} </a>:
            <a className="navbar-brand font7 all-link" href="#">{wesitename}</a>
           }
           
            {this.props.sidenavconst.visible?<button onClick={this.showsidebar.bind(this)} type="button" className="btn btn-primary all-link font1">{this.props.sidenavconst.topiclink}</button>:<a className="navbar-brand font7 all-link" href="#">{wesitename}</a>}
 
            <button className="navbar-toggler togglemenuO menuOpen" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <img src="https://img.icons8.com/ios/30/ffffff/menu.png"/>
            </button>
            <button className=" navbar-toggler togglemenuC menuClose" type="button">
            <img src="https://img.icons8.com/ios/25/ffffff/delete-sign.png"/>
            </button>
            <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                <div className="navbar-nav">
                {NavLink.map((x,i)=>
                    <Link key={i} href={x.url}><a className="nav-link nav-link-main active font1" >{x.urlname}</a></Link>
                    )}
                    <Link key='101' href={`/myprofile?id=${this.state.isuserlogin?this.state.user._id:''}`}><a className="nav-link nav-link-main active font1 isuserlogin"  >my-profile</a></Link>
                    <Link key='100'  href='?sign=true'><a className="nav-link nav-link-main active font1" onClick={this.showsignup.bind(this)} >sign-in</a></Link>
                    <a className="nav-link nav-link-main active font1 pointer" onClick={this.showfilter.bind(this)} >filter</a>
                    
             </div> </div>          
            </nav>
                */ } 
            <div className="ismobile_disable fixed show-fixed-bar">
            <NavBar state={this.state} showsignup={this.showsignup.bind(this)} showfilter={this.showfilter} showsidebar={this.showsidebar.bind(this)} sidenavconst={this.props.sidenavconst} />
        
            </div>

            <div className="ismobile_disable">
            <NavBar state={this.state} showsignup={this.showsignup.bind(this)} showfilter={this.showfilter} showsidebar={this.showsidebar.bind(this)} sidenavconst={this.props.sidenavconst} />
        
            </div>

            <div className="isdesktop_disable">
            <MobileNavBar state={this.state} showsignup={this.showsignup.bind(this)} showfilter={this.showfilter} showsidebar={this.showsidebar.bind(this)} sidenavconst={this.props.sidenavconst}  />
            </div>

            <div className="filter-item" >
            <Filter/>
            </div>
            </div>
            <style jsx>{
                `.subnav {
                    z-index : 100;
                  
                }
                .fixed {
                    position: -webkit-sticky;
                    width :100%;
                    position: fixed;
                    top: 0;
                    z-index:50;
                  }
                `
            }</style>
           </Layout>
           );
      }
    
}

export default Index;
