class Index extends Component {


    state = {
        name : 'chanaka',
        items : [],
        totalprice : 0,

    }
    componentDidMount(){

        console.log(Cookie.getJSON('faverite-item'));
        fetch(`${Url}itemforfavorite?fav=`)
        .then(res=>{ return res.json();})
        .then(data=>{ this.setState({items:data});})
    }

    updateOrder(id,qty){

        var tempitem = this.state.items;
        var itemsIndex = tempitem.findIndex(function(c) { 
            return c._id == id; 
        });

        tempitem[itemsIndex] = tempitem[itemsIndex].qty?tempitem[itemsIndex]:{...tempitem[itemsIndex],qty:0}
        tempitem[itemsIndex].qty = tempitem[itemsIndex].qty+qty;

        if(tempitem[itemsIndex].qty>=0){
            this.setState({
                items : [...tempitem],
                totalprice : this.gettotal()
            })
        }
     
    }

    gettotal(){
        var temparray = this.state.items;
        var total= 0;
        temparray.forEach(e => {
            total = ((e.price*(100-e.discount)/100)*e.qty)+total;
        });
        return total;
    }
    
    render() { 
        
        console.log('itemmm',this.state.items)
          return ( 
            <Layout>
              
                <div className="cart-menu">
                <div>
                    <h2 className="font1 topicColor menu2-topic">{this.props.topic}</h2>
                    
                </div>
                <div className="row">
                    <div className="col-lg-6 col-sm-12">
                  {  <CartList catageries={this.state.items} updateOrder={(id,qty)=>this.updateOrder(id,qty)}/>}
                    </div>
                 
                    <div className="col-lg-6 col-sm-12">
                    <OrderSide  cartItems={this.state.items} totalprice={this.state.totalprice} />
                    </div>
                </div>
                

                </div>
                <style jsx>
                {`
                .cart-menu{
                    margin : 20px;
                }
                
                `}</style>
                
                  </Layout>
           );
      }
    
}

export default Index;
