import React, { Component } from 'react';
import Link from 'next/link';
import Layout from './../layouts/MainLayout';
import $ from 'jquery';


class Index extends Component {

  componentDidMount= ()=> {

    $(document).ready(function() {
      $('.open-side-navbar').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable side-nav-visible');
      });

      $('.js-menu-close').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable').removeClass(' side-nav-visible');
      })
      $('.sidenavbar').click(function(){
        $('.js-side-nav-container').addClass('side-nav-animatable').removeClass(' side-nav-visible');
      })

    });


  }


  showSidebar=()=>{
    $(document).ready(function() {
        $('.js-side-nav-container').addClass('side-nav-animatable side-nav-visible');
    })
  }
  
  
render(){
  return(
<Layout>

<nav className="sidenavbar">
<div className="js-side-nav-container side-nav-container">
<div className="js-side-nav side-nav">
  <a href="#0" className="js-menu-close menu-close navbar-brand font1">&nbsp;
  <img src="https://img.icons8.com/windows/32/FF8C8C/delete-slide.png"/>&nbsp;
  MYBIZ.COM
  </a>
  <h2 className="sidenav-link-topic  font3"> &nbsp;<img src="https://img.icons8.com/ios/30/F7A12D/cd-collection.png"></img> &nbsp; {this.props.topic}</h2>
  <div className="sidenavbar-link">
                {this.props.sidenavlink.map(c=>
                <Link href={c.link}><a className="nav-link font3" ><img src="https://img.icons8.com/small/25/C4FBFE/arrow.png"/> &nbsp;{c.linkname}</a></Link>
                  )}
                </div>
</div>
</div>
</nav>

  <style jsx>
  {`

.sidenavbar{
  z-index:100;
}

.menu-close{
color: rgb(61, 228, 246);
font-size: 20px;
padding: 10px;
background: rgba(7, 36, 159 );
}
.side-nav {
position: relative;
transition: transform 0.35s ease-out;
background: rgba(7, 36, 159 ,0.9);
height: 100%;
width: 90%;
max-width: 300px;
box-shadow: 0 5px 6px 4px rgba(0, 0, 0, .2);
transform: translateX(-102%);
will-change: transform;

flex-direction: column;
}
.side-nav-container {
position: fixed;
left: 0;
top: 0;
width: 100%;
height: 100%;
overflow: hidden;
pointer-events: none;
}
.side-nav-container:before {
transition: opacity 0.3s cubic-bezier(0, 0, 0.3, 1);
content: '';
display: block;
position: absolute;
top: 0;
right: 0;
bottom: 0;
left: 0;
background: rgba(0, 0, 0, .4);
will-change: opacity;
opacity: 0;
pointer-events: none;
}
.side-nav-animatable .side-nav {
transition: transform 0.33s cubic-bexier(0, 0, 0.3, 0.1);
}
.side-nav-animatable.side-nav-visible .side-nav {
transition: transform 0.33s cubic-bezier(0, 0, 0.3, 1);
}
.side-nav-visible {
pointer-events: auto;
z-index : 10;
}
.side-nav-visible .side-nav {
transform: none;
}
.side-nav-visible:before {
opacity: 1;
}
.sidenav-link-topic {
  color: #F7A12D;
}
.sidenavbar-link{
    padding:10%;
    overflow: auto;
}
.sidenavbar-link a{
    color: #C4FBFE ;
    border-bottom: 1px solid rgba(0, 0, 0, 0.25) ;
}
.sidenavbar-link a:hover{
  transform: scale(1.01);
  transition: 0.5s;
  
}
  `}
  </style>
</Layout>
  )
}
 
}



export default Index;
