import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import Link from 'next/link';
import $ from 'jquery';


class Index extends Component {

    componentDidMount(){
        $(document).ready(function() {
            // Transition effect for navbar 
            $(window).scroll(function() {
              // checks if window is scrolled more than 500px, adds/removes solid class
              if($(this).scrollTop() > 300) { 
                  $('.navbar').addClass('SolidNav');
              }
              else {
                  $('.navbar').removeClass('SolidNav');
              }
            });
    });
    }
    render() { 
        
          return ( 
            <Layout>
            <div className="mainnav" >
      

            <nav class="navbar navbar-fixed-top navbar-inverse  navbar-toggleable-md">
    <button class="navbar-toggler navbar-toggler-right" type="button" data-toggle="collapse" data-target="#navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
    </button>
    <div class="d-flex">
        <a class="navbar-brand" href="#">Brand</a>
        <span class="navbar-text"><i class="fa fa-lg fa-tree"></i></span>
    </div>
    <div class="navbar-collapse collapse" id="navbarNav">
        <ul class="navbar-nav flex-row ml-auto justify-content-between">
            <li class="nav-item"><a href="#" class="nav-link">Home</a></li>
            <li class="nav-item"><a href="#" class="nav-link">Link</a></li>
            <li class="nav-item"><a href="#" class="nav-link">Link</a></li>
            <li class="nav-item"><a href="#" class="nav-link">Link</a></li>
            <li class="nav-item"><a href="#" class="nav-link">Link</a></li>
        </ul>
            </div>
        </nav>
              
            </div>
            
            <style jsx>
                {`
                .mainnav {
                    z-index : 100;
                  
                }
                .navbar-nav{
                    padding-left : 100px;
                }
                .nav-link {
                    background-color: black;
                }
                .navbar-nav a {
                    color : white;
                    
                }
                .navbar-nav a:hover {
                    color : gray;
                    
                }
               
                .navbar {
                    z-index : 100;
                    height: 80px;
                    background-color: transparent;
                    border: none;
                    color: white;
                    transition: background-color 2s ease 0s;
               }
              
                .navbar.SolidNav {
                    background-color: black;
                    transition: background-color 2s ease 0s;
                    box-shadow: 0 0 4px grey;
                    color: white;
               }
           
                `}
            </style>
                  </Layout>
           );
      }
    
}

export default Index;
