import React, { Component } from 'react';
import Layout from './../layouts/MainLayout';
import SubNavBar from './../layouts/SubNavbar';
import Footer from './../components/Footer';
import {Url} from './../constant/main';
import fetch from 'isomorphic-unfetch';

const About = ()=>{
  return(
    <div className="aboutus-section">
        <div className="container">
            <div className="row">
                <div className="col-md-3 col-sm-6 col-xs-12">
                    <div className="aboutus">
                        <h2 className="aboutus-title">About Us</h2>
                        <p className="aboutus-text">Aenean vulputate eleifend tellus. Aenean leo ligula, porttitor eu, consequat vitae, eleifend ac, enim. Aliquam lorem ante, dapibus in.</p>
                        <p className="aboutus-text">This is Photoshop's version of Lorem Ipsum. Proin gravida nibh vel velit auctor aliquet. Aenean sollicitudin, lorem quis bibendum auctor, nisi elit consequat ipsum, nec sagittis sem</p>
                        <a className="aboutus-more" href="#">read more</a>
                    </div>
                </div>
                <div className="col-md-3 col-sm-6 col-xs-12">
                    <div className="aboutus-banner">
                        <img src="http://themeinnovation.com/demo2/html/build-up/img/home1/about1.jpg" alt=""/>
                    </div>
                </div>
                <div className="col-md-5 col-sm-6 col-xs-12">
                    <div className="feature">
                        <div className="feature-box">
                            <div className="clearfix">
                                <div className="iconset">
                                    <span className="glyphicon glyphicon-cog icon"></span>
                                </div>
                                <div className="feature-content">
                                    <h4>Work with heart</h4>
                                    <p>Aenean vulputate eleifend tellus. Aenean leo ligula, porttitor eu, consequat vitae, eleifend ac, enim. Aliquam lorem ante, dapibus in.</p>
                                </div>
                            </div>
                        </div>
                        <div className="feature-box">
                            <div className="clearfix">
                                <div className="iconset">
                                    <span className="glyphicon glyphicon-cog icon"></span>
                                </div>
                                <div className="feature-content">
                                    <h4>Reliable services</h4>
                                    <p>Donec vitae sapien ut libero venenatis faucibu. Nullam quis ante. Etiam sit amet orci eget eros faucibus tincidunt</p>
                                </div>
                            </div>
                        </div>
                        <div className="feature-box">
                            <div className="clearfix">
                                <div className="iconset">
                                    <span className="glyphicon glyphicon-cog icon"></span>
                                </div>
                                <div className="feature-content">
                                    <h4>Great support</h4>
                                    <p>Aenean vulputate eleifend tellus. Aenean leo ligula, porttitor eu, consequat vitae, eleifend ac, enim. Aliquam lorem ante, dapibus in.</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <style jsx>{`
        body {
          font-family: 'Roboto';font-size: 1.1em;
      }
      
      .aboutus-section {
          padding: 90px 0;
      }
      .aboutus-title {
          font-size: 2em;
          letter-spacing: 0;
          line-height: 32px;
          margin: 0 0 39px;
          padding: 0 0 11px;
          position: relative;
          text-transform: uppercase;
          color: #000;
      }
      .aboutus-title::after {
          background: #fdb801 none repeat scroll 0 0;
          bottom: 0;
          content: "";
          height: 2px;
          left: 0;
          position: absolute;
          width: 54px;
      }
      .aboutus-text {
          color: #606060;
          font-size: 1em;
          line-height: 22px;
          margin: 0 0 35px;
      }
      
      a:hover, a:active {
          color: #ffb901;
          text-decoration: none;
          outline: 0;
      }
      .aboutus-more {
          border: 1px solid #fdb801;
          border-radius: 25px;
          color: #fdb801;
          display: inline-block;
          font-size: 1em;
          font-weight: 700;
          letter-spacing: 0;
          padding: 7px 20px;
          text-transform: uppercase;
      }
      .feature .feature-box .iconset {
          background: #fff none repeat scroll 0 0;
          float: left;
          position: relative;
          width: 18%;
      }
      .feature .feature-box .iconset::after {
          background: #fdb801 none repeat scroll 0 0;
          content: "";
          height: 150%;
          left: 43%;
          position: absolute;
          top: 100%;
          width: 1px;
      }
      
      .feature .feature-box .feature-content h4 {
          color: #0f0f0f;
          font-size: 1.1em;
          letter-spacing: 0;
          line-height: 22px;
          margin: 0 0 5px;
      }
      
      
      .feature .feature-box .feature-content {
          float: left;
          padding-left: 28px;
          width: 78%;
      }
      .feature .feature-box .feature-content h4 {
          color: #0f0f0f;
          font-size: 1.1em;
          letter-spacing: 0;
          line-height: 22px;
          margin: 0 0 5px;
      }
      .feature .feature-box .feature-content p {
          color: #606060;
          font-size: 1em;
          line-height: 22px;
      }
      .icon {
          color : #f4b841;
          padding:0px;
          font-size:2em;
          border: 1px solid #fdb801;
          border-radius: 100px;
          color: #fdb801;
          font-size: 1.5em;
          height: 70px;
          line-height: 70px;
          text-align: center;
          width: 70px;
      }
      }
        `}</style>
    </div>
  )
}

class Index extends Component {
  
    render() { 
        

          return ( 
           <Layout>
      <About/>

     <Footer></Footer>
  
                  </Layout>
           );
      }
    
}

Index.getInitialProps = async function(context) {
    const { id } = context.query;
    
    const res = await fetch(`${Url}types`);
    var  types = await res.json();
    var error = false;
    if(res.status!=200){
        error = true ;
   }
    return {itemname:id,types,error}


  }

export default Index;
